(@tafseer_category_id, '', 'في قولِه تعالى: {وَإِذْ غَدَوْتَ مِنْ أَهْلِكَ تُبَوِّئُ الْمُؤْمِنِينَ مَقَاعِدَ لِلْقِتَالِ}، الواقِعةُ المَذكُورةُ في الآيةِ هي:', '', 'يَومُ أُحُدٍ', '', 'يومُ الخَندَقِ', '', 'يومُ بَدْرٍ', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قال تعالى: {إِنْ يَمْسَسْكُمْ قَرْحٌ}، المَقصُودُ بـ(القَرْحِ):', '', 'الجِراحُ', '', 'الفَقْرُ', '', 'المَرَضُ', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قولِه تعالى: {ما كَانَ اللَّهُ لِيَذَرَ المؤْمِنِينَ عَلَى مَا أَنْتُمْ عَلَيْهِ حَتَّى يَمِيزَ الْخَبِيثَ مِنَ الطَّيِّبِ}، وقعَت (حتى) بعدَ (ما كانَ):', '', 'لإنكارِ اختِلاطِ الطَّيِّبِ بالخَبيثِ', '', 'للتَّأكيدِ على التَّمييزِ', '', 'للمُبالَغةِ في النفيِ', '', '', 'c', 'hard');
