INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'سببُ نُزولِ قولِ اللهِ تعالى: {لَقَدْ سَمِعَ اللَّهُ قَوْلَ الَّذِينَ قَالُوا إِنَّ اللَّهَ فَقِيرٌ وَنَحْنُ أَغْنِيَاءُ}، حادِثةٌ حصلَت بينَ أبي بكرٍ الصِّدِّيقِ رَضيَ اللهُ عنه ورجُلٍ كان مِن عُلماءِ اليَهودِ وأَحْبارِهم؛ اسمُه:', '', 'فِنْحاص', '', 'يَهُوذا', '', 'إِسرائيل', '', '', 'a', 'hard');
