(@tafseer_category_id, '', 'إذا كان للمُتوفَّى وَلدٌ فنَصِيبُ الوَالِدانِ مِن التَّرِكةِ:', '', 'النِّصفُ', '', 'الثُّلُثُ', '', 'السُّدُسُ', '', '', 'c', 'easy');
