(@tafseer_category_id, '', 'إذا لم يكُنْ للمُتوفَّى وَلدٌ، ووَرِثَه أبواهُ:', '', 'فلأُمِّه النِّصفُ ولأَبِيه ما تَبقَّى مِنَ المالِ', '', 'فلأُمِّه الثُّلُثُ ولأَبِيه ما تَبقَّى مِنَ التَّرِكةِ', '', 'فلأُمِّه الثُّلُثُ ولأبِيه النِّصفُ', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قبلَ تَشريعِ حَدِّ الزِّنا أمرَ اللهُ تعالى عِبادَه بعُقوبةِ مَن يَقعْنَ في الزِّنا بـ:', '', 'الرَّجْمِ', '', 'الجَلْدِ', '', 'الحَبسِ في البُيوتِ', '', '', 'c', 'easy');
