(@tafseer_category_id, '', 'في قولِه تعالى: {لَا تَأْكُلُوا أَمْوَالَكُمْ بَيْنَكُمْ بِالْبَاطِلِ}:', '', 'تحريمُ التَّعامُلِ المحرَّمِ ولو كان برِضًا منَ الطَّرَفَيْنِ', '', 'جَوازُ التَّعامُلاتِ المحرَّمةِ بالمالِ إذا كانت عن رضًا منَ الطرَفينِ', '', 'كَراهِيَةُ أَكلِ المالِ بالباطِلِ', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'إنْ كان للمُتوفَّى مع الأَبَوينِ الوَارثَينِ إِخوةٌ للميِّتِ -أشِقَّاءُ، أو لأبٍ، أو لِأُمٍّ- فتَستَحِقُّ الأمُّ:', '', 'الثُّلُثَ مِن التَّرِكةِ والباقي للأشِقَّاءِ', '', 'السُّدُسَ مِنَ التَّرِكةِ، وما بَقِيَ فللأَبِ', '', 'النِّصفَ مِنَ التَّرِكةِ والباقي للأبِ', '', '', 'b', 'easy');
