(@tafseer_category_id, '', 'قولُه تعالى: {فَمَنْ يَمْلِكُ مِنَ اللهِ شَيْئًا}، نوعُ الاستِفهامِ في الآيةِ:', '', 'استِفهامٌ للتَّعَجُّبِ', '', 'استِفهامٌ تَقريريٌّ', '', 'استِفهامٌ للإنكارِ والتَّوبيخِ', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قال اللهُ تعالى: {واحْذَرْهُمْ أَنْ يَفْتِنُوكَ عَنْ بَعْضِ مَا أَنْزَلَ اللَّهُ إِلَيْكَ}، هذه الآيةُ تدُلُّ على:', '', 'أنَّ الرَّسولَ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ مَعصُومٌ عنِ الخَطأِ والنِّسيانِ', '', 'أنَّ الكُفَّار حَريصُونَ كلَّ الحِرصِ على إضلالِ المُسلِمينَ عن دِينهِم', '', 'أنَّ الخَطأَ والنِّسيانَ جائِزانِ على الرَّسولِ ﷺ', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'يُستَفادُ مِن قَولِه تعالى: {إِلَّا الَّذِينَ تَابُوا مِنْ قَبْلِ أَنْ تَقْدِرُوا عَلَيْهِمْ} :', '', 'أنَّ التَّوبةَ لا أثَرَ لها في إِقامةِ حَدِّ الحِرابةِ', '', 'أنَّ التَّوبةَ تُسقِطُ حَدَّ الحِرابةِ سَواءٌ كانت قبلَ القُدرةِ عليهم أو بعدَها', '', 'أنَّه إذا كانتِ التوبةُ قَبلَ القُدرةِ عليهم تَمنَعُ من إقامةِ الحدِّ في الحِرابةِ', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قولِه تعالى: {أَفَحُكْمَ الْجَاهِلِيَّةِ يَبْغُونَ} نوعُ الاستِفهامِ:', '', 'استِفهامٌ حقيقيٌّ', '', 'استِفهامٌ تَقرِيريٌّ', '', 'استِفهامٌ إِنكارِيٌّ', '', '', 'c', 'hard');
