(@tafseer_category_id, '', 'مَنِ المَقصُودُ بقَولِه تعالى: {يُحَرِّفُونَ الْكَلِمَ مِنْ بَعْدِ مَوَاضِعِهِ}:', '', 'النَّصارى', '', 'المُنافِقونَ', '', 'اليَهودُ', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قولُه تعالى: {وَالْمُحْصَنَاتُ مِنَ الَّذِينَ أُوتُوا الْكِتَابَ} فيه:', '', 'تَحريمُ الزواجِ منَ الإماءِ مُطلقًا', '', 'أنَّ الإماءَ مِن أهلِ الكتابِ لا يُباحُ للمُسلمِ الزواجُ بهنَّ ولو خافَ العنَتَ', '', 'جَوازُ الزواجِ منَ الإماءِ الكِتابيَّاتِ', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قولِه تعالى: {وَلْيَحْكُمْ أَهْلُ الْإِنْجِيلِ}، مِن أنواعِ البَلاغةِ:', '', 'إطنابٌ', '', 'إيجازٌ بالحَذفِ', '', 'كِنايَةٌ', '', '', 'b', 'hard');
