(@tafseer_category_id, '', 'في قولِه تعالى: {وَمَا عَلَّمْتُمْ مِنَ الْجَوَارِحِ مُكَلِّبِينَ} كلمةُ: (مُكَلِّبِينَ) مَنصُوبةٌ لأنها:', '', 'تمييزٌ لـ (الجَوارح)', '', 'حَالٌ مِن فاعِلِ )عَلَّمْتُمْ)', '', 'مَفعُولٌ به لفعلٍ محذوفٍ', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قولِه تعالى: {فتَرَى الَّذِينَ فِي قُلُوبِهِمْ مَرَضٌ يُسَارِعُونَ فِيهِمْ} المقصودُ بالضميرِ في (فِيهِم) هم:', '', 'الأعرابُ', '', 'المُنافِقونَ', '', 'اليَهودُ والنَّصارى', '', '', 'c', 'hard');
