(@tafseer_category_id, '', 'في قولِه تعالى: {وَكَيْفَ يُحَكِّمُونَكَ وَعِنْدَهُمُ التَّوْرَاةُ} استفهامٌ:', '', 'للنَّفيِ', '', 'للتَّعَجُّبِ', '', 'للتَّوبِيخِ', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قال اللهُ تعالى: {ذَلِكَ فَضْلُ اللَّهِ يُؤْتِيهِ مَنْ يَشَاءُ وَاللَّهُ وَاسِعٌ عَلِيمٌ}، في جملةِ: (وَاللَّهُ وَاسِعٌ عَلِيمٌ) مِن أنْواعِ البلاغةِ:', '', 'تَشْبِيهٌ مُجمَلٌ للتَّوضِيحِ والتَّبيِينِ', '', 'اعتِراضٌ تَذْييليٌّ مُقرِّرٌ لِمَا قبلَه', '', 'تَقريرٌ على طَريقةِ الالتِفاتِ', '', '', 'b', 'hard');
