(@tafseer_category_id, '', 'حُكمُ الاصطِيادِ بالطُّيُورِ والسِّباعِ المُعَلَّمةِ:', '', 'مَكروهٌ', '', 'مُحرَّمٌ', '', 'جائِزٌ', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'إذا تحاكَمَ إلينا أهلُ الكتابِ؛ فإنَّنا:', '', 'نَحكُمُ بَينَهم بما أنزلَ اللهُ في القُرآنِ', '', 'نَحكُمُ بَينَهم بِشَرِيعَتِهم', '', 'نَحكُمُ بَينَهم بالعُرف', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'يُستَفادُ مِن قَولِه تعالى: {وَأَنْزَلْنَا إِلَيْكَ الْكِتَابَ بِالْحَقِّ مُصَدِّقًا لِمَا بَيْنَ يَدَيْهِ مِنَ الْكِتَابِ وَمُهَيْمِنًا عَلَيْهِ}:', '', 'أنَّ القُرآنَ ناسِخٌ لِمَا قَبلَه مِن الكتُبِ', '', 'أنَّ الكُتبَ المُنزَّلةَ مِن عندِ اللهِ كلَّها شَرائِعُ وأحْكامٌ واحِدةٌ لا اختِلافَ بينَها', '', 'أنَّ القُرآنَ الكريمَ تتمَّةٌ وتَكملةٌ لما في التَّوراةِ والإِنجِيلِ', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قولُه عزَّ وجلَّ -حكايةً عن موسى مخاطبًا بني إسْرائيلَ-: {وَآتَاكُمْ مَا لَمْ يُؤْتِ أَحَدًا مِنَ الْعَالَمِينَ}، المقصودُ بالعالَمينَ هنا:', '', 'كلُّ البشر التي سَبقتهُم', '', 'الإنسُ والجِنُّ', '', 'أهلُ زمانِهم', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قال اللهُ تعالى: {إِذْ هَمَّ قَوْمٌ أَنْ يَبْسُطُوا إِلَيْكُمْ أَيْدِيَهُمْ فَكَفَّ أَيْدِيَهُمْ عَنْكُمْ}، إظهارُ {أَيْدِيَهُمْ} في موقِعِ الإضْمارِ:', '', 'للتعجبِ', '', 'لزِيادةِ التَّقريرِ', '', 'للإنكارِ', '', '', 'b', 'hard');
