(@tafseer_category_id, '', 'في قَولِه تعالى: {وَإِذْ تَخْلُقُ مِنَ الطِّينِ كَهَيْئَةِ الطَّيْرِ بِإِذْنِي فَتَنْفُخُ فِيهَا فَتَكُونُ طَيْرًا بِإِذْنِي وَتُبْرِئُ الْأَكْمَهَ...} التعبيرُ بالمضارعِ عن أفعالٍ مضَتْ.. لماذا؟', '', 'لإفادةِ الاستِمرارِ', '', 'لأنَّ المُعجِزاتِ باقِيةٌ لا تَمُوتُ', '', 'لِتَصوِيرِ ذلك الماضِي وتَمثيلِه حاضِرًا في الذِّهْنِ', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'مَعنى (لَبَسْنَا) في قولِه تَعالَى: {وَلَلَبَسْنَا عَلَيْهِم مَّا يَلْبِسُونَ}', '', 'خلَطْنا عَليهِم أو ضلَّلْناهم', '', 'خلَقْنا لهم ما يَلبَسونَ', '', 'ألْبَسْناهم ثِيابَهم', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'يُستفادُ من قولِه تَعالَى: {إِلَّا أُمَمٌ أَمْثَالُكُم}:', '', 'تَوجيهُ المُؤمِنينَ إلى شُكرِ الله عزَّ وجلَّ على نِعمَةِ الإسلامِ', '', 'أنَّ أُمَّة الإسلامِ سَبقَها أُمَمٌ كثيرةٌ', '', 'تَنبيهٌ للمسلِمينَ على الرِّفقِ بالحيوانِ', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'يُستفادُ من قولِه تَعالَى: {وَمَنْ أَظْلَمُ}:', '', 'أنَّ الظُّلمَ درجاتٌ؛ بعضُه أشدُّ من بعضٍ؛ لأنَّ المعاصيَ تَتفاوَتُ', '', 'أنَّ الظالمينَ يُجزَوْن بجزاءٍ واحدٍ وإنِ اختلَفَ ظلْمُهم', '', 'أنَّ الظُّلمَ كلَّه بمنزلةٍ واحدةٍ', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'دلَّ قولُه تَعالَى: {وَلَوْ رُدُّواْ لَعَادُواْ لِمَا نُهُواْ عَنْهُ} على:', '', 'أنَّ الكافرينَ لا يَستَنزِهونَ من الكَذبِ حتى في الآخِرةِ', '', 'نَدمِ المُشركِينَ على تَفرِيطهِم يومَ القِيامَة', '', 'رغبةِ المشركينَ في الأعمالِ الصالحةِ', '', '', 'a', 'easy');
