(@tafseer_category_id, '', 'في قَولِه تَعالَى: {قَالَ أَنَاْ خَيْرٌ مِّنْهُ خَلَقْتَنِي مِن نَّارٍ وَخَلَقْتَهُ مِن طِين...} حجةٌ على:', '', 'الطَّبائِعيِّين', '', 'مَن يَفخَرون بأنْسابِهم', '', 'مَن يقولُ بأنَّ أصلَ الإنسانِ قِردٌ', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'يُستفادُ من قَولِه تَعالَى: {وَلَوْ أَنَّ أَهْلَ الْقُرَى آمَنُواْ وَاتَّقَواْ لَفَتَحْنَا عَلَيْهِم بَرَكَاتٍ مِّنَ السَّمَاء وَالأَرْضِ...}:', '', 'أنَّ التَّقوى نوعان: عامَّةٌ وخاصَّة', '', 'أنَّ المعاصيَ تمحَقُ بركةَ الدِّينِ والدُّنيا', '', 'أنَّ العقابَ على المعاصي يكونُ في الآخِرةِ فقط', '', '', 'b', 'easy');
