(@tafseer_category_id, '', 'مَعنى تَبخَسوا في قَولِه تَعالَى: {وَلَا تَبْخَسُواْ النَّاسَ أَشْيَاءهُمْ}:', '', 'تَخدَعُوا وتَمكُرُوا', '', 'تَحسُدُوا وتَحقِدُوا', '', 'تَنقُصوا وتَظلِموا', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'أفادَ قَولُه تَعالَى: {فَلَمَّا نَسُواْ مَا ذُكِّرُواْ بِهِ أَنجَيْنَا الَّذِينَ يَنْهَوْنَ عَنِ السُّوءِ وَأَخَذْنَا الَّذِينَ ظَلَمُواْ بِعَذَابٍ بَئِيسٍ بِمَا كَانُواْ يَفْسُقُون}:', '', 'أنَّ اللهَ إذا أنزَلَ العذابَ لا يَنْجو منه أحدٌ أبدًا', '', 'أنَّ العقوبةَ إذا نزلَتْ نجَا منها الآمِرونَ بالمعروفِ والناهونَ عنِ المنكَرِ', '', 'أنَّ اللهَ يَعْفو عنِ المُذنبينَ بسببِ مُجاوَرَتِهمُ الصالحينَ', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'تَضمَّنَ قَولُه تَعالَى: {وَلِبَاسُ التَّقْوَى ذَلِكَ خَيْرٌ}:', '', 'إشارةً لطيفةً إلى إخلاصِ العَملِ لله عزَّ وجلَّ', '', 'إيماءً إلى عُلوِّ رُتبةِ لِباسِ التَّقْوى وحُسنِ عاقبتِه', '', 'تَشبِيهًا بَليغًا؛ لتَشبِيه التَّقوى بالثِّياب', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'دلَّ قَولُه تَعالَى: {يَا بَنِي آدَمَ قَدْ أَنزَلْنَا عَلَيْكُمْ لِبَاسًا يُوَارِي سَوْءَاتِكُمْ وَرِيشًا} على:', '', 'أنَّ من دَلائلِ الإيمانِ الاهتمامَ بالباطِنِ دونَ الظَّاهِر', '', 'امتِنانِ اللهِ على بَني آدَمَ بلُبسِ الزينةِ فيدُلُّ على استِحْبابِها', '', 'كَراهيةِ الإسرافِ في اللِّباسِ', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'معنى قَولِه تَعالَى: {لَمْ يَدْخُلُوهَا وَهُمْ يَطْمَعُونَ}:', '', 'أنَّ أهلَ الأعْرافِ لم يَدْخُلوا الجنَّةَ بعدُ، ولكنَّهم يَطمَعونَ في دُخولِها', '', 'أنَّ أهلَ الأعرافِ دخَلوا الجنَّةَ بسببِ عدمِ طَمَعِهم', '', 'أنَّ الجنَّةَ محرَّمةٌ على مَن يطمَعُ في الدُّنيا', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'دلَّ قَولُه تَعالَى: {ذَلِكُمْ خَيْرٌ لَّكُمْ إِن كُنتُم مُّؤْمِنِين} على:', '', 'أنَّ التوحيدَ واجتنابَ نَزَعاتِ الشركِ ترفَعُ قَدرَ الإنسانِ وتُطهِّرُ عقلَه ونفْسَه من الخُرافاتِ والأوْهامِ', '', 'أنَّ الخيرَ مِن مُقتضَياتِ الإيمانِ لقَولِه: (إِن كُنتُم مُّؤْمِنِين)', '', 'أنَّ مِن ثَمراتِ الإيمان فِعلَ الخيراتِ وتَركَ المُنكراتِ', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'استدَلَّ العلماءُ بقَولِه تَعالَى: {اتَّبِعُواْ مَا أُنزِلَ إِلَيْكُم مِّن رَّبِّكُمْ} على:', '', 'أنَّ المباحَ ليس من خطابِ الشرعِ', '', 'أنَّ الأحْكامَ التَّكليفيةَ تَقتَصِرُ على الفِعلِ والتَّركِ فقط', '', 'أنَّ المباحَ مأمورٌ به لأنَّه من جملةِ ما أنزَلَ اللهُ', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قَوله تَعالَى: {وَهُوَ خَيْرُ الْحَاكِمِينَ}:', '', 'رَدٌّ على القَدريَّة الذين يقُولُون: لا قَدَرَ', '', 'أنَّه تَعالَى قد يُسمِّي بعضَ الأشْخاصِ حاكمًا لكنَّ الحاكمَ في الحقيقةِ هو اللهُ', '', 'إشارةٌ لطيفةٌ إلى إخلاصِ العِبادَةِ لله سُبحانه', '', '', 'b', 'medium');
