(@tafseer_category_id, '', 'الحكمةُ منَ البَدءِ في قصةِ مُوسَى عَليهِ السَّلامُ بذِكرِ نَتيجتِها في قَولِه تَعالَى: {ثُمَّ بَعَثْنَا مِن بَعْدِهِم مُّوسَى بِآيَاتِنَا إِلَى فِرْعَوْنَ وَمَلَئِهِ فَظَلَمُواْ بِهَا فَانظُرْ كَيْفَ كَانَ عَاقِبَةُ الْمُفْسِدِينَ}:', '', 'ذِكرُ المُقَدَّم والتَّشويقُ إلى المُؤَخَّر على سَبيلِ الالتِفاتِ', '', 'أنْ تكونَ متصِلةً بما يُناسِبُها منَ العِبَرِ في القصصِ التي قبلَها', '', 'إظهارُ التَّفنُّن في أَنواعِ البَلاغةِ القُرآنيَّة', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'دلَّ قَولُه تَعالَى: {وَإِذَا صُرِفَتْ أَبْصَارُهُمْ تِلْقَاءَ أَصْحَابِ النَّارِ} على:', '', 'أنَّ أصحابَ الأعرافِ بموضِعٍ قريبٍ من أهلِ الجنَّةِ', '', 'أنَّ أصحابَ الأعرافِ بمكانٍ مرتفعٍ بينَ الجنَّةِ والنارِ', '', 'أنَّ أصحابَ الأعرافِ بموضِعٍ قريبٍ من أهلِ النارِ', '', '', 'b', 'medium');
