(@tafseer_category_id, '', 'ذُكِرتِ الساعةُ في قَولِه تَعالَى: {وَلِكُلِّ أُمَّةٍ أَجَلٌ فَإِذَا جَاء أَجَلُهُمْ لَا يَسْتَأْخِرُونَ سَاعَةً وَلَا يَسْتَقْدِمُونَ} وإن كان دونَها كذلك:', '', 'لأنَّها كانت أقلَّ اسمٍ للأوْقاتِ في العُرفِ', '', 'لأنَّ مِن عادةِ العربِ أنَّ ما كان أقَلُّ مِن الساعةِ فلا يُعبَأُ بِه', '', 'إشارةً إلى يومِ القِيامَة', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'سببُ تحريمِ الأشياءِ التي ذكَرَها اللهُ تَعالَى في قَولِه: {قُلْ إِنَّمَا حَرَّمَ رَبِّيَ الْفَوَاحِشَ مَا ظَهَرَ مِنْهَا وَمَا بَطَنَ...}', '', 'لشدَّةِ ضرَرِها بالإنسانِ', '', 'لضررٍ ثابتٍ لازمٍ لها', '', 'لعلَّةٍ عارضةٍ عليها', '', '', 'b', 'medium');
