(@tafseer_category_id, '', 'مَعنى (مُتَبَّرٌ) في قَولِه تَعالَى: {إِنَّ هَؤُلاء مُتَبَّرٌ مَّا هُمْ فِيهِ وَبَاطِلٌ مَّا كَانُواْ يَعْمَلُونَ}:', '', 'مقطوع', '', 'هالكٌ', '', 'مُتبَرَّؤٌ مِنه', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'حُكِمَ بنَفيِ العلمِ عن أكثَرِ آلِ فِرعونَ في قَولِه تَعالَى: {وَلَكِنَّ أَكْثَرَهُمْ لاَ يَعْلَمُونَ}:', '', 'لبَيانِ شُيوعِ الجَهلِ فيهِم', '', 'للدَّلالةِ على شِدَّة غَفلتِهم', '', 'لأنَّ القليلَ منهم علِمَ كمؤمنِ آلِ فِرعونَ وآسيةَ امرأةِ فِرعونَ', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'سببُ عطفِ النهيِ على الفسادِ في الأرضِ على الأمرِ بذِكرِ آلاءِ اللهِ في قَولِه تَعالَى: {فَاذْكُرُواْ آلاءَ اللَّهِ وَلَا تَعْثَوْا فِي الأَرْضِ مُفْسِدِينَ}:', '', 'الإرشادُ إلى أنَّ تذكُّرَ الآلاءِ يبعَثُ على الشكرِ والطاعةِ وتَركِ الفسادِ', '', 'لأنَّ النِّعمَ تزيدُ بالشُّكرِ', '', 'للمُبالغةِ في النهيِ عنِ الإفْسادِ في الأرضِ', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'لم يُذكَرِ الفاعلُ في قَولِه تَعالَى: {فَغُلِبُواْ هُنَالِكَ وَانقَلَبُواْ صَاغِرِينَ} ولم يَقُلْ: فغلَبَهم مُوسَى:', '', 'لأنَّ ذلك لم يكُنْ بكَسبِ مُوسَى عَليهِ السَّلامُ', '', 'لأنَّ موسى كان قد اعتَزلهُم حين عَبدُوا العِجلَ ولم يكُ معهُم', '', 'لأنَّهم قَومُه ومِنهم أَهلُه وعَشيرتُه فلم تُنسَب له الغَلبَة تَلطُّفًا به', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قولِ اللهِ تَعالَى: {وَمَا تَنقِمُ مِنَّا إِلاَّ أَنْ آمَنَّا بِآيَاتِ رَبِّنَا لَمَّا جَاءتْنَا رَبَّنَا أَفْرِغْ عَلَيْنَا صَبْرًا وَتَوَفَّنَا مُسْلِمِينَ} دليلٌ على:', '', 'أنَّ الإيمانَ والإسلامَ واحدٌ', '', 'أنَّ الإسلامَ أعمُّ منَ الإيمانِ', '', 'أنَّ الإيمانَ فرَعٌ من فروع الإسلامِ', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قَولِه تَعالَى: {أَلاَ لَهُ الْخَلْقُ وَالأَمْرُ} ردٌّ على:', '', 'القائلينَ بخَلقِ القرآنِ', '', 'الذين قالوا بحُدوثِ العالَمِ', '', 'القائلينَ بوَحْدةِ الوجودِ', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'أشارَ قَولُه تَعالَى: {أَوْ تَقُولُواْ إِنَّمَا أَشْرَكَ آبَاؤُنَا مِن قَبْلُ وَكُنَّا ذُرِّيَّةً مِّن بَعْدِهِمْ} إلى:', '', 'أنَّ شأنَ الذُّرِّيَّةِ الاقتداءُ بالآباءِ', '', 'أنَّ شَرفَ النَّسَبِ لا يُغنِي عن صاحِبِه شَيئًا ما لم يَعمَل بطاعةِ  الله عزَّ وجلَّ', '', 'مُنافاةِ وَجهِ الشَّبَهِ بينَ الآباءِ والذُّرِّيَّةِ', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'استدَلَّ العلماءُ بقَولِه تَعالَى: {اسْجُدُواْ لآدَمَ}:', '', 'أنَّ السُّجودَ هو أَشرفُ العِباداتِ', '', 'أنَّ الأمرَ لا يَقتَضي الوُجوبَ', '', 'أنَّ صيغةَ (افعَلْ) تأْتي في أصْلِ وَضعِها للوُجوبِ', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'ذُكِرت إقامةُ الوُجوهِ في قَولِه تَعالَى: {وَأَقِيمُواْ وُجُوهَكُمْ عِندَ كُلِّ مَسْجِدٍ}:', '', 'لأنَّ الوجهَ أشرَفُ ما في الجسدِ', '', 'تمثيلًا لكمالِ الإقبالِ على اللهِ تَعالَى في مواضِعِ عبادتِه', '', 'للإشارةِ إلى دَيمومَةِ التَّعبُّدِ لله عزَّ وجلَّ', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'الغرَضُ من الاستِفهامِ في قَولِه تَعالَى: {أَهَؤُلاء الَّذِينَ أَقْسَمْتُمْ}:', '', 'التَّوبيخِ', '', 'التعجُّبِ', '', 'التَّقريرِ', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'أفادَ قَولُه تَعالَى: {فَقَالَ يَا قَوْمِ اعْبُدُواْ اللَّهَ مَا لَكُم مِّنْ إِلَهٍ غَيْرُهُ}:', '', 'أنَّ مِن الحِكمَةِ في الدَّعوةِ التَّنوُّعَ بين التَّبشيرِ والإنذارِ', '', 'أنَّ أوَّلَ الواجباتِ التَّوحيدُ', '', 'أنَّ العِباداتِ تَوقِيفِيَّة', '', '', 'b', 'hard');
