(@tafseer_category_id, '', 'عبَّرَ عنِ الإصابةِ بالماضي في قَولِه تَعالَى: {أَن لَّوْ نَشَاء أَصَبْنَاهُم بِذُنُوبِهِمْ}:', '', 'للتَّلطُّفِ بهم وإظهارِ الرَّحمةِ', '', 'للإشارةِ إلى سُرعةِ الهَلاكِ', '', 'للدَّلالةِ على مُضيِّ الحدَثِ', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'كان جوابُ موسى بعُنفٍ وغِلظةٍ في قَولِه تَعالَى: {قَالَ إِنَّكُمْ قَوْمٌ تَجْهَلُونَ}:', '', 'لأنَّ ذلك المناسبُ لحالِهم', '', 'ليَردعَهم عن فِعلِهم', '', 'لشِدَّةٍ كانت في طَبعِه', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'الاستفهامُ في قَولِه تَعالَى: {أَتَقُولُونَ عَلَى اللَّهِ مَا لاَ تَعْلَمُون}:', '', 'للتعجُّبِ', '', 'للطلبِ', '', 'للإنْكارِ والتوْبيخِ', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'استدَلَّ العُلَماءُ بقَولِه تَعالَى: {أَفَأَمِنُواْ مَكْرَ اللّهِ فَلاَ يَأْمَنُ مَكْرَ اللّهِ إِلاَّ الْقَوْمُ الْخَاسِرُون} على:', '', 'إثباتِ اسمِ الله القاهِر سُبحانه', '', 'أنَّ الأمنَ من مكرِ اللهِ منَ الكبائرِ', '', 'وُجوبِ الإيمان والتَّصديقِ بجميعِ أَفعالِ الله عزَّ وجلَّ', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'المرادُ بدارِ الفاسقينَ في قَولِه تَعالَى: {سَأُرِيكُمْ دَارَ الْفَاسِقِينَ}:', '', 'دِيارُ قومِ لوطٍ', '', 'دِيارُ عادٍ وثمودَ وأضرابِهم منَ الكافرينَ', '', 'دِيارُ آلِ فِرعونَ', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'المقصودُ منَ النَّهيِ بعدَ الأمرِ بالاتِّباعِ في قَولِه تَعالَى: {اتَّبِعُواْ مَا أُنزِلَ إِلَيْكُم مِّن رَّبِّكُمْ وَلاَ تَتَّبِعُواْ مِن دُونِهِ أَوْلِيَاء}:', '', 'تأكيدُ مُقتَضى الأمرِ باتِّباعِ ما أُنزِلَ عَليهِم', '', 'النَّهيُ عنِ الوُقوعِ في الذُّنوبِ والمعاصي', '', 'الدَّعوةُ إلى تحقيقِ العُبوديةِ للهِ', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'دلَّ قَولُه تَعالَى: {وَتَوَفَّنَا مُسْلِمِينَ} على:', '', 'أنَّ الأنبياءَ ليسُوا مَعصُومِين', '', 'أنَّه لا يوجَد خُلودٌ إلَّا في الجنَّة فقط', '', 'أنَّ الدِّينَ عندَ اللهِ الإسلامُ في كلِّ زمانٍ ومكانٍ', '', '', 'c', 'hard');
