(@tafseer_category_id, '', 'أفرَدَ اللهُ تَعالَى البغيَ عنِ الإثمِ في قَولِه: {وَالإِثْمَ وَالْبَغْيَ بِغَيْرِ الْحَقِّ}:', '', 'لأنَّه إِحدَى سُبُلِ الشِّركِ', '', 'لتَعاظُمِه في الإثمِ', '', 'للمُبالَغةِ في الزَّجرِ عنه', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'دلَّ قَولُه تَعالَى: {واَسْأَلْهُمْ عَنِ الْقَرْيَةِ الَّتِي كَانَتْ حَاضِرَةَ الْبَحْرِ...} على:', '', 'أنَّ المَشَقَّة تَجلِبُ التَّيسِيرَ', '', 'أنَّ الحِيَلَ في تحليلِ الأمورِ التي حرَّمها الشَّرعُ محرَّمةٌ', '', 'أنَّ كَثرةَ النِّعَمِ قد تكونُ ابتلاءً مِن الله لعِبادِه', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'يُستفادُ من قَولِه تَعالَى: {وَلَقَدْ خَلَقْنَاكُمْ ثُمَّ صَوَّرْنَاكُمْ ثُمَّ قُلْنَا لِلْمَلآئِكَةِ}:', '', 'التَّأكيدُ على ضَعفِ الإنسانِ بتَذكيرهِ بمراحِل خَلقِه', '', 'إثباتُ الصفاتِ الاختياريةِ للهِ كصفةِ الكلامِ هنا', '', 'إظهار التَّدرجِ في خَلقِ بني آدمَ إشارةً إلى كَراهَةِ العَجَلَة', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'مَعنى تُنظِرونِ في قَولِه تَعالَى: {قُلِ ادْعُواْ شُرَكَاءكُمْ ثُمَّ كِيدُونِ فَلاَ تُنظِرُونِ}:', '', 'تُبصرونِي', '', 'تَنتصِرُون عليَّ', '', 'تؤخِّرونِي', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'دلَّ قَولُه تَعالَى: {ثُمَّ اسْتَوَى عَلَى الْعَرْشِ} على:', '', 'أنَّ الاستواءَ بمَعنى الاستيلاءِ', '', 'أنَّ اللهَ سُبحانه خَلقَ العَرشَ قبلَ الأرضِ', '', 'بُطلانِ تأويلِ الاستواءِ بمَعنى المُلكِ', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'الغرَضُ منَ النِّداءِ في قَولِه تَعالَى: {قَالَ يَا مُوسَى إِنِّي اصْطَفَيْتُكَ عَلَى النَّاسِ}:', '', 'للتَّنبيهِ', '', 'لطَلَبِ الإقْبالِ', '', 'للتَّأْنيسِ وإزالةِ الرَّوْعِ', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قوله تعالى: {فَلَمَّا تَرَاءَتِ الْفِئَتَانِ نَكَصَ عَلَى عَقِبَيْهِ}، الفِئَتانِ هُما:', '', 'المُتَقاتِلتان مِنَ المُؤمِنينَ', '', 'الفُرس والرُّوم', '', 'المُؤمِنونَ والكُفَّارُ', '', '', 'c', 'easy');
