(@tafseer_category_id, '', 'قوله تعالى: {وَمَا كَانُوا أَوْلِيَاءَهُ إِنْ أَوْلِيَاؤُهُ إِلَّا الْمُتَّقُونَ}، أي: وما كانَ كُفَّار قُرَيش أولياء:', '', 'اللَّه عَزَّ وجَلَّ', '', 'المَسجِد الحَرام', '', 'مُحَمَّد صلَّى اللهُ عليه وسلَّم', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قوله تعالى: {يَا أَيُّهَا الَّذِينَ آمَنُوا أَطِيعُوا اللَّهَ وَرَسُولَهُ وَلَا تَوَلَّوْا عَنْهُ}، أي:', '', 'ولا تُعرِضوا عَنهُ، ولا تَترُكوا طاعَتَه', '', 'لا تُوالوا غَيرَه', '', 'لا تَترُكوه حال الجِهاد', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'إنَّما خَرَجَ رَسولُ اللَّه ﷺ والمُسلِمونَ يُريدونَ عِير قُرَيش، حَتَّى جَمع اللَّه بَينَهم وبَينَ عَدُوِّهم على غَيرِ ميعاد، وذلك في غَزوة:', '', 'بَدر', '', 'أحُد', '', 'الحُدَيبية', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَوله: {إِنْ هَذَا إِلَّا أَسَاطِيرُ الْأَوَّلِينَ} أي:', '', 'مَكرٌ وخِداعٌ', '', 'أباطيلُ وتُرَّهاتٌ', '', 'الكُتُبُ القَديمة', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قوله تعالى: {فَثَبِّتُوا الَّذِينَ آمَنُوا}، هذا الكَلام مُوَجَّه:', '', 'لِلمُهاجِرينَ', '', 'لِلأنصارِ', '', 'لِلمَلائِكة', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَوله تَعالَى: {يَسْأَلُونَكَ عَنِ الْأَنْفَالِ} أي:', '', 'يَسألُك يا مُحَمَّد أصحابُك عَن حُكم الغَنائِم', '', 'يَسألُك يا مُحَمَّد أصحابُك عَن حُكم صَلاةِ النَّوافِل', '', 'يَسألُك أصحابُك يا محمد عن نِصابِ الزَّكاةِ', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَوله: {وَإِمَّا تَخَافَنَّ مِنْ قَوْمٍ فَانْبِذْ إِلَيْهِمْ عَلَى سَوَاءٍ}، فيه مَجيء (قَوم) نَكِرة في سياق الشَّرط لِإفادة:', '', 'العُموم', '', 'الحَصرِ', '', 'التَّأكيد', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَوله: {وَإِذْ يَمْكُرُ بِكَ الَّذِينَ كَفَرُوا لِيُثْبِتُوكَ}، معنى (يُثْبِتُوكَ):', '', 'يَحبِسوك ويَسجُنوك', '', 'يُخرِجوك', '', 'يَنفوك', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَوله: {لِيَمِيزَ اللَّهُ الْخَبِيثَ مِنَ الطَّيِّبِ}، المَقصود بِالطَّيِّبِ:', '', 'العَمَل الصَّالِح', '', 'المُؤمِنونَ', '', 'النَّافِع مِن كُلِّ شَيء', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَوله: {وَلَوْ أَسْمَعَهُمْ لَتَوَلَّوْا وَهُمْ مُعْرِضُونَ}، فيه دَلالة عَلَى:', '', 'أنَّه سُبحانَه يَعلَمُ الشَّيء قَبلَ كَونِه', '', 'أنَّهم لَم يَكونوا يَسمَعونَ', '', 'أنَّهم مُصِرُّونَ على الكُفرِ', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قوله تعالى: {ذَلِكَ بِأَنَّهُمْ شَاقُّوا اللَّهَ وَرَسُولَهُ}، معناه:', '', 'كَفَروا بِاللَّه ورَسوله', '', 'كَذَّبوا بما جاءَ بِه الرَّسول عَنِ اللَّهِ تعالى', '', 'حارَبوا، أو خالَفوا وجانَبوا', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَوله تَعالَى: {فَاسْتَجَابَ لَكُمْ أَنِّي مُمِدُّكُمْ بِأَلْفٍ مِنَ الْمَلَائِكَةِ مُرْدِفِينَ}، مَعنَى (مُردِفينَ):', '', 'راكِبينَ', '', 'أقوياء', '', 'مُتَتابِعينَ', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'معنى (مُوهِن) في قوله تعالى: {ذَلِكُمْ وَأَنَّ اللَّهَ مُوهِنُ كَيْدِ الْكَافِرِينَ}:', '', 'مُهلِك', '', 'مانِعٌ', '', 'مُضْعِف', '', '', 'c', 'easy');
