(@tafseer_category_id, '', 'رِباطُ الخَيلِ هو:', '', 'أنواعُ الخَيلِ', '', 'كَثرة الخَيلِ', '', 'رَبطُها وحَبسُها، واقتِناؤُها لِلجِهادِ في سَبيلِ اللَّه تَعالَى', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'الإسلامُ يَهدِمُ ما كانَ قَبلَه بِنَصِّ الكِتابِ العَزيزِ، وهو قوله:', '', '{قُلْ لِلَّذِينَ كَفَرُوا إِنْ يَنْتَهُوا يُغْفَرْ لَهُمْ مَا قَدْ سَلَفَ} [الأنفال: 38]', '', '{وَمَا كَانَ اللَّهُ لِيُعَذِّبَهُمْ وَأَنْتَ فِيهِمْ وَمَا كَانَ اللَّهُ مُعَذِّبَهُمْ وَهُمْ يَسْتَغْفِرُونَ} [الأنفال: 33]', '', '{وَقَاتِلُوهُمْ حَتَّى لَا تَكُونَ فِتْنَةٌ وَيَكُونَ الدِّينُ كُلُّهُ لِلَّهِ} [الأنفال: 39]', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'ثَبات القَلب أصلُ ثَباتِ البَدَن، دَليلُه مِنَ القُرآنِ:', '', '{وَمَا جَعَلَهُ اللَّهُ إِلَّا بُشْرَى} [الأنفال: 10]', '', '{إِذْ جَاءَ رَبَّهُ بِقَلْبٍ سَلِيمٍ} [الصافات: 84]', '', '{وَلِيَرْبِطَ عَلَى قُلُوبِكُمْ} [الأنفال: 11]', '', '', 'c', 'easy');
