(@tafseer_category_id, '', 'مَعنَى الأنفال:', '', 'الأنعام مِن إبِل وبَقر ونَحوها', '', 'الصَّلَوات غَير المَفروضة', '', 'الغَنائِم', '', '', 'c', 'easy');
