(@tafseer_category_id, '', 'كانَ عَدَد المُسلِمينَ يَومَ بَدر:', '', 'ثلاثَ مائة وتِسعةَ عَشَرَ', '', 'مِائَتَيْنِ وتِسعةَ عَشَرَ', '', 'ثلاثَ مِائة', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَوله تَعالَى: {إِذْ تَسْتَغِيثُونَ رَبَّكُمْ فَاسْتَجَابَ لَكُمْ أَنِّي مُمِدُّكُمْ بِأَلْفٍ مِنَ الْمَلَائِكَةِ مُرْدِفِينَ}، نَزَلتْ في غَزوة:', '', 'بَدر', '', 'الأحزاب', '', 'أحُد', '', '', 'a', 'medium');
