(@tafseer_category_id, '', 'عَمَّمَ اللَّه تعالى الخِطاب في قَولِه: {وَقَاتِلُوهُمْ حَتَّى لَا تَكُونَ فِتْنَةٌ}:', '', 'للتَّأكيدِ على الوَعيدِ', '', 'لِزيادة تَرغيبِ المُؤمِنينَ في القِتالِ', '', 'لتَرهيبِ المُنافِقينَ مِن مُعاوَنةِ الكافِرينَ', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَول اللَّه تَعالَى: {وَلَا تَكُونُوا كَالَّذِينَ قَالُوا سَمِعْنَا وَهُمْ لَا يَسْمَعُونَ}، السَّمع الَّذي نَفاه اللَّه تعالى عنهم:', '', 'حاسَّةُ السَّمع', '', 'الاستِماعُ للآيات', '', 'المَعنَى المُؤَثِّر في القَلبِ', '', '', 'c', 'medium');
