(@tafseer_category_id, '', 'رِجز الشَّيطانِ هو:', '', 'كُفره', '', 'وَساوِسه', '', 'نَجاسَتُه', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قوله تعالى: {وَإِذْ يَمْكُرُ بِكَ الَّذِينَ كَفَرُوا}، أسنَدَ المَكرَ إلى جَميعِ الكافِرينَ، وإن كانَ الَّذينَ تَوَلَّوُا المَكرَ هم سادة المُشرِكينَ وكُبَراؤُهم:', '', 'لِأنَّهم جَميعًا على مِلَّة واحِدة', '', 'اتِّباعًا للقاعِدَة: أنَّ الحَسنةَ تَخُصُّ والسَّيِّئةَ تَعُمُّ', '', 'لِأنَّ البَقيةَ كانوا أتباعًا لِلزُّعَماءِ؛ يَأْتَمِرونَ بِأمرِهم', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قوله تعالى: {فَاضْرِبُوا فَوْقَ الْأَعْنَاقِ}، أي: فاضرِبوا أيُّها:', '', 'المُسلِمونَ', '', 'المَلائِكة', '', 'المُقاتِلونَ', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قوله تعالى: {وَإِنْ يَعُودُوا فَقَدْ مَضَتْ سُنَّتُ الْأَوَّلِينَ}، معناه:', '', 'وإنْ يَعُد هَؤُلاءِ المُشرِكونَ إلى كُفرِهم؛ فَإنَّما يَتَأسَّوْنَ بِالأوَّلينَ', '', 'وإنْ يَعُدْ هَؤُلاءِ المُشرِكونَ مِن قُرَيش إلى قِتالِك؛ فَقَد مَضَت طَريقَتُنا إهلاك غَيرِهم مِنَ الأُمَمِ الماضيةِ', '', 'لا تَعودوا إلى المَعاصي كَما كانَ مَن سَلَفَ مِن قَبلِكم مِنَ الأُمَمِ', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قوله تعالى: {كَذَّبُوا بِآيَاتِ رَبِّهِمْ فَأَهْلَكْنَاهُمْ بِذُنُوبِهِمْ}، المَقصود:', '', 'قَومُ عاد', '', 'قَوم فِرعَون والذين مِن قبْلِهم', '', 'كُفَّار قُرَيش', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَوله تَعالَى: {وَمَا كَانَ صَلَاتُهُمْ عِنْدَ الْبَيْتِ إِلَّا مُكَاءً وَتَصْدِيَةً}، مَعنَى مُكاءً:', '', 'كَذبًا', '', 'صَفيرًا', '', 'رِياء', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قوله تعالى: {تَخَافُونَ أَنْ يَتَخَطَّفَكُمُ النَّاسُ}، مَعناه:', '', 'تَخافونَ أن يَسرِقَكم النَّاسُ', '', 'تَخافونَ أن يَستَلِبَكم الكُفَّار، فَيَأخُذوكم بِسُرعة', '', 'تَخافونَ أن يَقتُلَكم النَّاسُ', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قوله تعالى: {يَا أَيُّهَا الَّذِينَ آمَنُوا إِذَا لَقِيتُمُ الَّذِينَ كَفَرُوا زَحْفًا} أي:', '', 'مُتَقارِبينَ بَعضكم إلى بَعض', '', 'مَشيًا', '', 'مُقاتِلينَ لهم', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قالَ رَسولُ اللَّه ﷺ: (أمَا عَلِمتَ أنَّ الإسلامَ يَهدِمُ ما كانَ قَبلَه)، المُخاطَب هو:', '', 'أبو هُرَيرةَ', '', 'خالِد بن الوَليد', '', 'عَمرو بن العاصِ', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'مَجيء الفِعلِ بِصيغة المُضارِعِ في قَولِهِ: {يَسْأَلُونَكَ عَنِ الْأَنْفَالِ}، دالٌّ عَلَى:', '', 'قِدَم السُّؤال', '', 'حَداثة السُّؤالِ', '', 'تَكَرُّر السُّؤال', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'مِن مَقاصِد سورة الأنفالِ:', '', 'بَيانُ أسبابِ النَّصرِ، وبَعضِ أحكامِ الجِهادِ', '', 'بَيان أحكامِ الصِّيامِ', '', 'ذِكْر أحكام الزَّكاةِ', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قوله تعالى: {إِنَّ شَرَّ الدَّوَابِّ عِنْدَ اللَّهِ الصُّمُّ الْبُكْمُ الَّذِينَ لَا يَعْقِلُونَ}، مَعناهُ:', '', 'إنَّ شَرَّ ما دَبَّ على الأرضِ هَؤُلاءِ الكُفَّار ؛ فَهم صُمٌّ عَن سَماعِ الحَقِّ، بُكْم عَنِ التَّكَلُّمِ بِه، لا يَعقِلونَ عَنِ اللَّه مَواعِظَه', '', 'تَشبيهٌ لِلكُفَّارِ بِالأنعامِ الَّتي لا تَسمَعُ ولا تُخرِجُ صَوتًا، وهي لا تَعقِلُ', '', 'احذَروا أيُّها المُسلِمونَ مِن التَّشَبُّه بِالأنعامِ الَّتي لا تَسمَعُ ولا تَعقِلُ', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قوله تعالى: {وَإِنْ تَنْتَهُوا فَهُوَ خَيْرٌ لَكُمْ}، معناه:', '', 'وَإن تَنتَهوا أيُّها المُسلِمونَ عَنِ المَعاصي فَهو خَيرٌ لَكم في الدُّنيا والآخِرة', '', 'وَإن تَنتَهوا -يا كَفَّار قُرَيش- عَنِ الشِّركِ والكُفرِ بِاللَّهِ، والتَّكذيبِ لِرَسولِه، وقِتال نَبيِّه ﷺ والمُؤمِنينَ بِه؛ فَهو خَيرٌ لَكم في الدُّنيا والآخِرة', '', 'وَإن تَنتَهوا أيُّها النَّاسُ عَمَّا يُغضِبُ اللَّه فَهو خَيرٌ لَكم في الدُّنيا والآخِرة', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'معنى {تَسْتَفْتِحُوا} في قوله تعالى: {إِنْ تَسْتَفْتِحُوا فَقَدْ جَاءَكُمُ الْفَتْحُ}:', '', 'تُقاتِلوا', '', 'تَستَنصِروا، طلَبُ الفتْحِ', '', 'تَفتَحوا البِلادَ بالإسلامِ', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'الحِكمة في قِتال المَلائِكة مع النَّبيِّ ﷺ، مع أنَّ جِبريلَ وَحدَه قادِرٌ على أن يَدفَعَ الكُفَّار بِريشة مِن جَناحِه، هي:', '', 'لتَقوِيَة قُلوبِ المؤمنينَ بمُعاونتِهم في النَّصرِ', '', 'لِأنَّهم حارَبوا المُشرِكينَ في صُورة البَشَرِ، فَلَمْ يَكونوا بقوتِهم نَفْسِها وهم على صورَتِهم الحَقيقيةِ', '', 'رِعايةً لِصورة الأسبابِ وسُنَّتها الَّتي أجراها اللَّه تَعالَى في عِبادِه', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'معنى {يُثْخِنَ} في قوله: {مَا كَانَ لِنَبِيٍّ أَنْ يَكُونَ لَهُ أَسْرَى حَتَّى يُثْخِنَ فِي الْأَرْضِ}:', '', 'يَعفوَ ويَصفَحَ', '', 'يُبالِغَ في قَتلِ المُشرِكينَ ويَقهَرَهم', '', 'يَجتَهِد في إِعمارِ الأَرضِ', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قوله تعالى: {فَلَا تُوَلُّوهُمُ الْأَدْبَارَ} معناه:', '', 'لا تَترُكوا قِتالَهم', '', 'لا تُعطوهُم ما يُريدونَ', '', 'لا تَفِرُّوا مِنهُم، وتُعطوهم ظُهورَكم', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'السِّين والتَّاء في (اسْتَجِيبُوا) في قوله تعالى: {يَا أَيُّهَا الَّذِينَ آمَنُوا اسْتَجِيبُوا لِلَّهِ وَلِلرَّسُولِ}:', '', 'لِلتَّأكيدِ', '', 'لِلتَّمييزِ', '', 'لِلتَّعقيبِ', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَوله: {لَهُمْ دَرَجَاتٌ عِنْدَ رَبِّهِمْ}، تَنوين (دَرَجاتٌ):', '', 'لِلتأكيدِ', '', 'لِلتَّعظيمِ', '', 'لِلتَّعريفِ', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَوله: {وَإِذْ يَعِدُكُمُ اللَّهُ إِحْدَى الطَّائِفَتَيْنِ أَنَّهَا لَكُمْ}، الطَّائِفَتَانِ هُما:', '', 'النَّصر أو الشَّهادة', '', 'العِير أو النَّفير', '', 'الغَنائِم أو فَتح مَكَّة', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَوله: {وَتَوَدُّونَ أَنَّ غَيْرَ ذَاتِ الشَّوْكَةِ تَكُونُ لَكُمْ}، ذات الشَّوكة هي:', '', 'ذات الحَدِّ والسِّلاحِ؛ مِنَ السَّيف والسِّنانِ والنِّصال', '', 'ذات النَّصرِ والظَّفَرِ', '', 'ذاتُ القوَّةِ العاليةِ', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'سَبَب نُزولِ قَولِه تَعالَى: {وَمَا رَمَيْتَ إِذْ رَمَيْتَ وَلَكِنَّ اللَّهَ رَمَى}:', '', 'أمر رَسولُ اللَّه ﷺ يَومُ بَدر فَأخَذَ كَفًّا مِن الحَصباءِ فاستَقبَلنا بِه وجوه الكافرين، فَرَماهم بِها، وقال: (شاهَتِ الوُجوه)، فانهَزَموا', '', 'قول النَّبي ﷺ يَومَ بَدر: «إذا أكثبوكم فَعليكم بالنَّبْل»', '', 'اطَّلَعَ النَّبيُّ ﷺ على أهل القَليب، فَقالَ: «وَجَدتُم ما وعَدَ رَبُّكم حَقًّا؟»', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'اختيار (الفُرقانِ) في قَولِه: {إِنْ تَتَّقُوا اللَّهَ يَجْعَلْ لَكُمْ فُرْقَانًا}:', '', 'لدَلالةِ تلك اللَّفظةِ على كلِّ معاني الفَوزِ والظَّفرِ', '', 'لرعاية فواصل الآيات', '', 'لِقَصد شُموله ما يَصلُحُ لِلمَقامِ مِن مَعانيه', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَول اللَّه تَعالَى: {سَأُلْقِي فِي قُلُوبِ الَّذِينَ كَفَرُوا الرُّعْبَ}، التَّعبير بِإلقاء الرُّعب في القَلبِ وليس بـ(رَعَبْته أو أرعَبتُه):', '', 'لتَخصِيصهِم بالرُّعبِ دونَ المؤمنينَ', '', 'لتَأكيدِ وُصولِ الرُّعبِ إلى قُلوبِهم', '', 'لِما في التَّعبيرِ بِإلقاءِ الرُّعبِ مِنَ الإشعارِ بِأنَّه يُصَبُّ في القُلوبِ دُفعةً واحِدةً', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قُتِلَ في غَزوة بَدرٍ مِنَ المُشرِكينَ:', '', 'سَبعونَ', '', 'خَمسونَ', '', 'سِتُّونَ', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'حُكم أخْذ الأنفال في الأُمَمِ قَبلَنا:', '', 'الكَراهة', '', 'الحُرْمة', '', 'الحِلُّ', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قوله تعالى: {وَلَكِنْ لِيَقْضِيَ اللَّهُ أَمْرًا كَانَ مَفْعُولًا}، تَنكير {أَمْرًا}:', '', 'لِلتَّعظيمِ', '', 'لِلتَّمييزِ', '', 'لِلتَّعريفِ', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'جملة: {إِنَّهُمْ لَا يُعْجِزُونَ} في قوله: {وَلَا يَحْسَبَنَّ الَّذِينَ كَفَرُوا سَبَقُوا إِنَّهُمْ لَا يُعْجِزُونَ}:', '', 'جُملةٌ مُستَأنفة لا مَحَلَّ لَها مِنَ الإعرابِ', '', 'جُملة صِفة مَرفوعةٍ على الابتداءِ', '', 'جُملةٌ حاليةٌ في مَحلِّ نَصبٍ', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'تَقديم الجار والمَجرور في قَولِه تعالى: {وَلِتَطْمَئِنَّ بِهِ قُلُوبُكُمْ}، يُفيدُ:', '', 'التَّمييز', '', 'اللُّزوم', '', 'الِاختِصاص', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'الِاستِفهام في قوله: {وَمَا لَهُمْ أَلَّا يُعَذِّبَهُمُ اللَّهُ}:', '', 'تَقريريٌّ', '', 'إخباريٌّ', '', 'إنكاريٌّ', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'معنى {فَيَرْكُمَهُ} في قوله: {وَيَجْعَلَ الْخَبِيثَ بَعْضَهُ عَلَى بَعْضٍ فَيَرْكُمَهُ جَمِيعًا}:', '', 'يَجمَعَه', '', 'يُفنيَه', '', 'يُهلِكَه', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'تَجريدُ الفِعلِ (رَمَى) عَنِ المَفعولِ بِه في قَولِهِ: {وَمَا رَمَيْتَ إِذْ رَمَيْتَ وَلَكِنَّ اللَّهَ رَمَى}:', '', 'للدَّلالةِ على قُوَّةِ الرَّميِ وشِدَّتِه', '', 'لِأنَّ المَقصود الأصليَّ بَيانُ حال الرَّمي', '', 'للتَّأكيدِ على الفِعلِ لنِيابَتِه عن الفاعِلِ', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَوله: {فَإِنْ يَكُنْ مِنْكُمْ مِائَةٌ صابِرَةٌ يَغْلِبُوا مِائَتَيْنِ وَإِنْ يَكُنْ مِنْكُمْ أَلْفٌ يَغْلِبُوا أَلْفَيْنِ}، ذُكِرَ الصَّبر في أوَّل جُملةٍ، وحُذِفَ من الثَّانيةِ:', '', 'لدَلالةِ السَّابقةِ عليه', '', 'لأنَّ الأُولَى حالُ قِتالٍ والثانيةَ حالُ نَصرٍ', '', 'لأنَّ الصَّبرَ المَأُجورَ عليهِ عندَ الصَّدمةِ الأُولَى', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'الضميرُ في قوله تعالى: {وَيُثَبِّتَ بِهِ الْأَقْدَامَ}، عائد على:', '', 'المَلائِكة', '', 'النُّعاس', '', 'المَطَر', '', '', 'c', 'hard');
