(@tafseer_category_id, '', 'الاستِفهامُ في قولِ اللهِ تعالى: {كَيْفَ يَكُونُ لِلْمُشْرِكِينَ عَهْدٌ عِنْدَ اللَّهِ}، استِفهامٌ بمعنى:', '', 'التَّوبيخِ', '', 'التَّقريرِ', '', 'الإنكارِ والاستِبعادِ', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَوله تعالى: {قُلْ أَتُنَبِّئُونَ اللَّهَ بِمَا لَا يَعْلَمُ فِي السَّمَوَاتِ وَلَا فِي الْأَرْضِ}، استِفهام على سَبيلِ:', '', 'التَّقرير', '', 'التَّعَجُّب', '', 'التَّهَكم والتَّوبيخ', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', '«لا إلَهَ إلَّا اللَّه العَظيم الحَليم، لا إلَهَ إلَّا اللَّه رَبُّ العَرشِ العَظيم، لا إلَهَ إلَّا اللَّه رَبُّ السَّمَواتِ، ورَبُّ الأرضِ، ورَبُّ العَرشِ الكَريم»، هذا الدُّعاء يُقالُ عِندَ:', '', 'الاستِسقاءِ', '', 'الكَرْبِ', '', 'النَّومِ', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَولُه تعالى: {حَتَّى إِذَا أَخَذَتِ الْأَرْضُ زُخْرُفَهَا}، معنى {زُخْرُفَهَا} :', '', 'ما أُمِرَت بِه', '', 'حُسْنَها وبَهاءَها', '', 'مَكانَها بين الكَواكِب', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَولُه تعالى: {فَقَدْ لَبِثْتُ فِيكُمْ عُمُرًا مِنْ قَبْلِهِ}، لَبِثَ النبيُّ ﷺ قبْلَ البعثةِ في قُريشٍ:', '', 'خَمسينَ سَنةً', '', 'أربَعينَ سَنةً', '', 'خَمسًا وأربَعينَ سَنةً', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَولُه تعالى: {وَمَا تُغْنِي الْآيَاتُ وَالنُّذُرُ عَنْ قَوْمٍ لَا يُؤْمِنُونَ}، المُراد بِالنُّذُرِ:', '', 'الرُّسُلُ المُنذِرونَ', '', 'التَّحذير في الكُتُبِ المُنَزَّلة', '', 'الآيات الكَونية', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَولُه تعالى: {وَإِذَا مَسَّ الْإِنْسَانَ الضُّرُّ دَعَانَا لِجَنْبِهِ}، مَعنَى (دَعانا لِجَنبِه):', '', 'سَألَنا وهو مُضطَجِع على جَنبِه', '', 'سَألنا لِكَشفِ ضُرِّه', '', 'سَألَنا أن نَكونَ بقُرْبه', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَولُه تعالى: {وَجَاءَتْهُمْ رُسُلُهُمْ بِالْبَيِّنَاتِ} معنى (البَيِّنات):', '', 'العَذابُ المُبِين', '', 'المُعجِزات والبَراهين الواضِحة', '', 'الكُتُب التي أَوحَى اللهُ إليهم', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَوله تعالى: {إِنِّي أَخَافُ إِنْ عَصَيْتُ رَبِّي عَذَابَ يَوْمٍ عَظِيمٍ}، فيه إيراد اليَومِ بِالتَّنوينِ التَّفخيميِّ، ووَصفه بِالعِظَمِ:', '', 'لِلَفْتِ الانتباهِ المُؤْذِن بالتَّحذيرِ', '', 'للتَّخصيصِ والحَصرِ', '', 'لِتَهويلِ ما فيه مِنَ العَذابِ وتَفظيعِه', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَولُه تعالى: {قُلْ لَوْ شَاءَ اللَّهُ مَا تَلَوْتُهُ عَلَيْكُمْ}، المَتلُوُّ هو:', '', 'التَّوراة', '', 'القُرآن الكَريم', '', 'الإنجيل', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَولُه تعالى: {قُلْ فَانْتَظِرُوا إِنِّي مَعَكُمْ مِنَ الْمُنْتَظِرِينَ}، أي: انْتَظِروا:', '', 'عَذابَ اللَّه تعالى', '', 'فتحَ مكَّة', '', 'نَصْرَ المُؤمِنينَ', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَولُه تعالى: {إِنَّهُ لَا يُفْلِحُ الْمُجْرِمُونَ}، المَقصود بِقَولِهِ: (المُجرِمونَ):', '', 'المُنافِقونَ', '', 'أهلُ الكِتابِ', '', 'الكافِرونَ', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَولُه تعالى: {وَقَدَّرَهُ مَنَازِلَ}، مَعناه:', '', 'تَقديرُ مَسيرِ الأَرضِ في الكَونِ لظُهورِ اختِلاف الفُصولِ', '', 'إِنزالُ الشَّمسِ مَنازِلها مِن المشرقِ والمغربِ لتَعاقُبِ اللَّيلِ والنَّهارِ', '', 'قَدَّرَ اللَّه مَسِير القَمَرِ في مَنازِلَ يَنزِلُ في كُلِّ يَوم ولَيلة مَنزِلًا مِنها', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَولُه تعالى: {ثُمَّ جَعَلْنَاكُمْ خَلَائِفَ فِي الْأَرْضِ}، الخِطاب مُوَجَّهٌ:', '', 'لذُرِّيَّةِ آدمَ عليه السَّلامُ', '', 'لِأُمَّة النَّبيِّ مُحَمَّد ﷺ', '', 'لبَنِي إِسرائيلَ', '', '', 'b', 'easy');
