(@tafseer_category_id, '', 'قَولُه تعالى: {وَيَقُولُونَ هَؤُلَاءِ شُفَعَاؤُنَا عِنْدَ اللَّهِ}، القائِلونَ هُم:', '', 'أهلُ الكِتابِ', '', 'المُنافِقونَ', '', 'المُشرِكونَ', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَول اللَّه تَعالَى: {وَمَا كَانَ النَّاسُ إِلَّا أُمَّةً وَاحِدَةً فَاخْتَلَفُوا}، فيه إخبارٌ بِأنَّ الحَقَّ واحِد، وأنَّ ذلك الِاختِلافَ:', '', 'مَذمومٌ', '', 'مَشرُوعٌ', '', 'فِطرِيٌّ', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قوله تعالى: {وَيَجْعَلُ الرِّجْسَ عَلَى الَّذِينَ لَا يَعْقِلُونَ} الرِّجسُ هو:', '', 'العَذاب', '', 'النَّجَسُ', '', 'الخَوفُ', '', '', 'a', 'easy');
