(@tafseer_category_id, '', 'قَولُه تعالى: {إِنَّ الَّذِينَ لَا يَرْجُونَ لِقَاءَنَا وَرَضُوا بِالْحَيَاةِ الدُّنْيَا}، مَعنَى (لا يَرجونَ):', '', 'لا يَستَعِدُّونَ', '', 'لا يُريدُونَ', '', 'لا يَتَوَقَّعونَ', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قالَ تَعالَى: {إِنَّ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ يَهْدِيهِمْ رَبُّهُمْ بِإِيمَانِهِمْ}، جُعِلَ الإيمانُ وَحدَه سَبَبَ الهِداية:', '', 'لِأنَّ الإيمانَ يَشملُ الإقرارَ والقَولَ والعَملَ فجمعَ كلَّ الجَوارِح', '', 'لِأنَّه هو الباعِثُ النَّفسيُّ لها', '', 'لِأنَّ أساسَ الدِّينِ هو الإيمانُ وبدُونِه يَحبطُ العَملُ', '', '', 'b', 'medium');
