(@tafseer_category_id, '', 'سَمَّى سُبحانَه الشَّمسَ سِراجًا وضياءً:', '', 'مِن بابِ التَّعبيرِ  بالسَّببِ عن المُسبِّب', '', 'لأنَّها تُسرَجُ باللَّيلِ وتُضِيءُ بالنَّهارِ فجَمعَت بينهُما', '', 'لِأنَّ فيها مَعَ الإنارة والإشراقِ تَسخينًا وإحراقًا، فَهي بِالنَّارِ أشبَهُ', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قوله تعالى: {أَكَانَ لِلنَّاسِ عَجَبًا أَنْ أَوْحَيْنَا إِلَى رَجُلٍ مِنْهُمْ}، تَقديمُ خَبَرِ (كانَ) على اسمِها:', '', 'لِلِاهتِمامِ بِه', '', 'لِلتَّأكيدِ', '', 'لِلِاستِفهامِ', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَولُه تعالى: {قُلْ لَوْ شَاءَ اللَّهُ مَا تَلَوْتُهُ عَلَيْكُمْ وَلَا أَدْرَاكُمْ بِهِ}، معنى (أَدْرَاكُمْ):', '', 'أَعلَمكُم وأخبَرَكم', '', 'بَارَكَكُم وهَداكُم', '', 'سَأَلَكُم وطالَبَكُم', '', '', 'a', 'medium');
