(@tafseer_category_id, '', 'قَوله تَعالَى: {لِيَجْزِيَ الَّذِينَ آمَنُوا وَعَمِلُوا الصَّالِحَاتِ بِالْقِسْطِ}، لَمْ يُعَيِّنِ اللَّه تَعالَى ثَوابَ المُؤمِنينَ وجَزاءَهم:', '', 'لِأنَّه تَعالَى يَتَوَلَّى إثابة المُؤمِنينَ بِما يَليقُ بِلُطفِه وكَرَمِه', '', 'لشَحْذِ أَذهانِهم في تَخيُّلِ عَطاءِ الله عزَّ وجلَّ', '', 'لأنَّ الجنَّة فيها ما لا عَينٌ رأتْ ولا أُذنٌ سَمِعَت', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قَولِه تعالى: {فَلَمَّا أَنْجَاهُمْ إِذَا هُمْ يَبْغُونَ فِي الْأَرْضِ}؛ زيادة (في الأرضِ) لِلدَّلالة على:', '', 'تَعيينِ بَغيِهم على الأرضِ', '', 'شُمول بَغيِهم لِأقطارِها', '', 'قَصرِ بَغيِهم في الأرضِ', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَوله تعالى: {وَبَشِّرِ الَّذِينَ آمَنُوا أَنَّ لَهُمْ قَدَمَ صِدْقٍ عِنْدَ رَبِّهِمْ}، أي: سابِقةً ومَنزِلةً رَفيعة؛ سُمِّيتْ قَدَمًا:', '', 'لِلدَّلالة على صِدقِهم', '', 'لِأنَّ السَّبقَ بِها', '', 'إشارةً إلى سَعيِهم عليها في سَبيلِ الله بالدُّنيا', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَولُه تعالى: {فَإِنْ فَعَلْتَ فَإِنَّكَ إِذًا مِنَ الظَّالِمِينَ}، المَقصود بِالفِعلِ هو:', '', 'الشِّرك', '', 'المَعصية', '', 'تَرْك الدَّعوة', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَوله تعالى: {لَقَدْ جَاءَكَ الْحَقُّ مِنْ رَبِّكَ}، جُملة مُستَأنِفة استِئنافًا بَيانيًّا، وقُرِنَتِ الجُملة بِحَرفَيِ التَّأكيدِ، وهُما:', '', '(مِنْ) و (قَدْ)', '', 'لام القَسَمِ و(قَدْ)', '', '(مِنْ) ولام القَسَمِ', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', '«كُنتُ خَلفَ رَسولِ اللَّه ﷺ يَومًا، فَقالَ: يا غُلامُ، إنِّي أُعَلِّمُكَ كَلِمات: احفَظِ اللَّهَ يَحفَظْكَ، احفَظِ اللَّهَ تَجِدْه تُجاهَكَ:» القائِل هو:', '', 'مُعاذ بن جَبَل', '', 'عَبد اللَّه بن عُمَر', '', 'عَبد اللَّه بن عَبَّاس', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَوله تَعالَى: {فَمَنْ أَظْلَمُ مِمَّنِ افْتَرَى عَلَى اللَّهِ كَذِبًا أَوْ كَذَّبَ بِآيَاتِهِ}، المَقصود مِن قوله: {أَوْ كَذَّبَ بِآيَاتِهِ}:', '', 'إِخبارُهم بما يَسوءُهُم يومَ القِيامَة', '', 'إلحاقُ الوَعيدِ الشَّديدِ بهم', '', 'تَهديهُم بالعَذابِ رجاءَ أن يَتوقَّفُوا عن تَكذيبِ النبيِّ ﷺ', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَولُه تعالى: {فَلَا تَكُونَنَّ مِنَ الْمُمْتَرِينَ}، معنى {الْمُمْتَرِينَ}:', '', 'المُكَذِّبينَ', '', 'الكُفَّارُ', '', 'المُتَرَدِّدينَ', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَولُه تعالى: {وَلَقَدْ بَوَّأْنَا بَنِي إِسْرَائِيلَ مُبَوَّأَ صِدْقٍ} أي:', '', 'رِزقًا كثيرًا مُباركًا', '', 'كِتابًا صادِقًا؛ والمَقصُود: التَّوراة', '', 'مُنزَلًا مَحمودًا مُختارًا', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَوله تعالى: {وَمَا كَانُوا لِيُؤْمِنُوا}، الضَّمير عائِد على:', '', 'أهلِ الكِتابِ', '', 'المُنافِقينَ', '', 'أهل مَكَّة', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَولُه تعالى: {وَرَضُوا بِالْحَيَاةِ الدُّنْيَا}، فيه مَحذوف، تَقديرُه:', '', 'وَرَضُوا بِالحَياةِ الدُّنيا تَكذيبًا بالآخِرة', '', 'وَرَضُوا بالحَياةِ الدُّنيا مِنَ الآخِرة', '', 'وَرَضُوا بالحَياةِ الدُّنيا فَرَحًا بها', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَولُه تعالى: {وَيَعْبُدُونَ مِنْ دُونِ اللَّهِ مَا لَا يَضُرُّهُمْ وَلَا يَنْفَعُهُمْ} قَدَّمَ (ما لا يَضُرُّهم) على (ما لا يَنفَعُهم):', '', 'لِأنَّ دَفعَ الضُّرِّ أَشَقُّ مِن جَلبِ النَّفعِ', '', 'لِأنَّ العِبادة تُقامُ لِلمَعبودِ خَوفًا مِن العِقابِ أوَّلًا، ثُمَّ رَجاءً لِلثَّوابِ بعد ذلك', '', 'لِأنَّ مَضرَّةَ الدُّنيا تَسبِقُ نَعيمَ الجِنانِ في الآخرةِ', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَوله: {إِنَّهُ لَا يُفْلِحُ الْمُجْرِمُونَ}، فيه تَأكيد الجُملة بِحَرفِ التَّأكيدِ (إنَّ)؛ وذلك:', '', 'لِبَيانِ حَصرِ العُقوبَةِ على المُجرِمينَ تَشنِيعًا عليهم', '', 'للِتَأكيدِ التَّخصِيصِي', '', 'لِشُمولِ المُجْرِمُونَ وعُمومه لِلمُخاطَبِينَ', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'الإذْن في قَولِه تَعالَى: {وَمَا كَانَ لِنَفْسٍ أَنْ تُؤْمِنَ إِلَّا بِإِذْنِ اللَّهِ}، إذَنٌ:', '', 'قَدَريٌّ', '', 'شَرعيٌّ', '', 'كَونيٌّ', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَوله تَعالَى: {وَلَوْ شَاءَ رَبُّكَ لَآمَنَ مَنْ فِي الْأَرْضِ كُلُّهُمْ جَمِيعًا}، التَّأكيد بـ(كُلُّهُمْ)؛ لِلتَّنصيصِ على:', '', 'التَّأكيد', '', 'التَّعيين', '', 'العُموم', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَال اللَّه تَعالَى: {قُلْ يَا أَيُّهَا النَّاسُ إِنْ كُنْتُمْ فِي شَكٍّ مِنْ دِينِي}، وهُم كُفَّارٌ يَعتَقِدونَ بُطْلان ما جاءَ بِه:', '', 'لِأنَّهم لَمَّا رَأوُا الآيات اضطَرَبوا وشَكُّوا في أمرِه ﷺ', '', 'لِأنَّه لا يَجوزُ لهُم مُجَرَّد الشَّكِّ فَكَيفَ بِالكُفرِ؟!', '', 'لِأنَّه حَتَّى لَو كانَ فيهم شَكٌّ يَجِبُ أنْ يَزولَ', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قوله تعالى: {قَدْ جَاءَكُمُ الْحَقُّ مِنْ رَبِّكُمْ} وَصف الحَقِّ بـ{مِنْ رَبِّكُمْ}:', '', 'تَنويهٌ بِأنَّه حَقٌّ مُبينٌ، لا يَخلِطُه باطِل ولا رَيْبَ؛ فَهو مَعصومٌ مِن ذلك', '', 'لتَشريفِ الحَقِّ بنِسبَتِه إلى الله تبارك وتعالى', '', 'تَأكيدٌ على صِدقِ النَّبيِّ صلَّى اللهُ عليه وسلَّم', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَوْل اللَّه تَعالَى: {وَإِذَا مَسَّ الْإِنْسَانَ الضُّرُّ}، جاءَ (الضُّرُّ) بِالألِف واللَّامِ:', '', 'لِلتَّعريفِ', '', 'للعُموم والشُّمول', '', 'لِأنَّه إشارة إلى ما تَقَدَّمَ مِنَ الشَّرِّ', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَوله: {وَلَا تَكُونَنَّ مِنَ الَّذِينَ كَذَّبُوا بِآيَاتِ اللَّهِ}، هذا النَّهي مِن بابِ:', '', 'التَّهييج والإلهاب', '', 'التَّحذيرِ', '', 'التَّعظيم', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَوله تعالى: {وَأَنْ أَقِمْ وَجْهَكَ لِلدِّينِ حَنِيفًا}، اللَّام في (لِلدِّينِ):', '', 'لِلشُّمولِ', '', 'لِلعِلَّة', '', 'لِلتَّعريفِ', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَوله تعالى: {وَلَا تَكُونَنَّ مِنَ الْمُشْرِكِينَ}، تَأكيد الفِعلِ المَنهي عَنه بِنونِ التَّوكيدِ:', '', 'لِلمُبالَغة في النَّهيِ عن الشِّرك', '', 'لِلتَّأكيدِ على خُطورةِ الشِّرك', '', 'لِلنهي عن عُمومِ الشِّرك', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَوله تعالى: {وَإِذَا مَسَّ الْإِنْسَانَ الضُّرُّ}، وَصْف لِلمُستَقبَلِ، وقَوله تعالى: {فَلَمَّا كَشَفْنَا}، لِلماضي:', '', 'لبَيان أنَّه هَكَذا كانَ فيما مَضَى، وهَكَذا يَكونُ في المُستَقبَلِ', '', 'لِلدَّلالة على أنَّه مُتَحقَّقٌ لا مَحالة', '', 'للإشارةِ إلى قولِه تعالى: {وَتِلْكَ الْأَيَّامُ نُدَاوِلُهَا بَيْنَ النَّاسِ}', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَوله: {إِنَّ رُسُلَنَا يَكْتُبُونَ مَا تَمْكُرُونَ}، جاء {تَمْكُرُونَ} بِالتَّاء على الخِطابِ:', '', 'مُبالَغة لَهم في الإعلامِ بِحال مَكرهم', '', 'ليَشملَ جَميعَ أَنواعِ المكرِ الناتِج عنهُم', '', 'لِلدلالةِ على تَجدُّدِ مَكرِهم واستِمرارِه', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قالَ اللَّه تَعالَى: {أَنْ أَنْذِرِ النَّاسَ وَبَشِّرِ الَّذِينَ آمَنُوا أَنَّ لَهُمْ قَدَمَ صِدْقٍ}، قَدَّمَ الإنذار على التَّبشيرِ:', '', 'لِأنَّ التَّخليةَ مُقَدَّمة على التَّحليةِ، وإزالة ما لا يَنبَغي مُقَدَّم في الرُّتبة على فِعل ما يَنبَغي', '', 'لِأنَّ الخَوفَ مِن المَضارِّ أوْلَى في التَّقديمِ لِلوُصولِ إلى المَنافِعِ', '', 'لِأنَّ الخِطابَ في أوَّلِه مُوجَّهٌ لِلكُفَّارِ، فَقَدَّمَ الإنذار على التَّبشيرِ', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'لَمَّا كانَ يَومُ فَتْحِ مَكَّة أمَّنَ رَسولُ اللَّه صلَّى اللهُ عليه وسلَّم النَّاسَ، إلَّا أربَعة نَفَر؛ هُم:', '', 'عِكرِمة بن أبي جَهل، وعَبد اللَّه بن خَطَل، وأبو سُفيان، وعَبد اللَّه بن سَعد بن أبي السَّرح', '', 'عَمرو بن العاص، وعَبد اللَّه بن خَطَل، ومَقيس بن صُبابة، وعَبد اللَّه بن سَعد بن أبي السَّرح', '', 'عِكرِمة بن أبي جَهل، وعَبد اللَّه بن خَطَل، ومَقيس بن صُبابة، وعَبد اللَّه بن سَعد بن أبي السَّرح', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَوله تعالى: {وَهُوَ خَيْرُ الْحَاكِمِينَ}، ثَناء وتَذييل؛ لِما فيه مِن:', '', 'العُموم', '', 'الكَمال', '', 'التَّعظيمِ', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قوله تعالى: {وَإِذَا تُتْلَى عَلَيْهِمْ آيَاتُنَا بَيِّنَاتٍ} بُنِيَ {تُتْلَى} للمفعول:', '', 'لبَيان أنَّ جَميعَ آياتِ القُرآنِ مُعجزةٌ', '', 'إيذانًا بِتَكذيبِهم عِندَ تِلاوة أيِّ تالٍ يتلوها', '', 'إشارةً إلى عَظيمِ أَجرِ التِّلاوةِ', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَوله: {يَا أَيُّهَا النَّاسُ إِنْ كُنْتُمْ فِي شَكٍّ مِنْ دِينِي}، فيه إيثار الخِطَاب باسْم الجِنس مُصَدَّرًا بِحَرف التَّنبيه:', '', 'لِأنَّ النَّبيَّ صلَّى اللهُ عليه وسلَّم خاتَم الأَنبياءِ والرُّسُل', '', 'تَعميمًا لِلتَّبليغِ، وإظهارًا لِكَمالِ العِناية بِشَأن ما بُلِّغوا به', '', 'لِأنَّهم ِالوَحْي جاءَ عامًّا للثَّقلَينِ: الإنسِ والجِنِّ', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قوله تعالى: {ثُمَّ نُنَجِّي رُسُلَنَا وَالَّذِينَ آمَنُوا} في صِيغة الِاستِقبال {نُنَجِّي} لِحِكاية الأحوالِ الماضيةِ:', '', 'تَحفيز لِلمُؤمِنينَ', '', 'تَهويل لِأمرِها بِاستِحضار صُوَرِها', '', 'دَليل على الِاستِمرارِ', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَولُه تعالى: {إِلَيْهِ مَرْجِعُكُمْ}، فيه تَقديم الجار والمَجرور (إلَيهِ)؛ لِإفادة:', '', 'الجَزم', '', 'العُموم', '', 'القَصر', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَوله تعالى: {يُفَصِّلُ الْآيَاتِ لِقَوْمٍ يَعْلَمُونَ}، جُملة مُستَأنِفة ابتِدائية مَسوقة..', '', 'لِلحَثِّ على العِلم', '', 'لِتَبيينِ النِّعمة', '', 'لِلِامتِنانِ بِالنِّعمة', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قوله تعالى: {وَإِذَا أَذَقْنَا النَّاسَ رَحْمَةً مِنْ بَعْدِ ضَرَّاءَ مَسَّتْهُمْ إِذَا لَهُمْ مَكْرٌ فِي آيَاتِنَا}، أي..', '', 'وَإذا أنزَلنا المطر على النَّاسِ مِن بَعدِ القَحْطِ والجَفافِ إذا هُم يَنسبُون الخيرَ والمطرَ للنُّجومِ والكواكبِ مِن دُونِ الله سُبحانه', '', 'وَإذا فَرَّجنا عَنِ المُشرِكينَ ورَحِمناهم، مِن بَعدِ بَلاءٍ أصابَهم، سَعَوْا بِكُلِّ حيلة بِالباطِلِ؛ لِإبطالِ آياتنا ورَدِّها وتَكذيبِها', '', 'وَإذا أذَقنا النَّاسَ نِعمة مِن بَعدِ بَلاءٍ إذا هُم يُنكِرونَ نِعَمَنا التي أَسبَغناهُم بها', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'تَقديمُ تَرْكِ عِبادة الآخرين على عِبادَتِه تَعالَى في قَولِه تعالى: {فَلَا أَعْبُدُ الَّذِينَ تَعْبُدُونَ مِنْ دُونِ اللَّهِ وَلَكِنْ أَعْبُدُ اللَّهَ}:', '', 'لِأنَّهم كانوا يَعرِفون المِلةَ الحَنِيفيَّة', '', 'لِتَقَدُّم التَّخلية على التَّحليةِ', '', 'لِأنَّ الفعلَ مُقدَّمٌ على الترْك', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'مِن أهم مَقاصِد سُورةِ هودٍ:', '', 'حَثُّ النَّاسِ على الإنفاقِ في سَبيلِ اللَّهِ تعالى', '', 'تَثبيت قَلْب النَّبيِّ صلَّى الله عليه وسلَّم وتَسليتُه', '', 'ذِكر الصَّومِ وشُروط صِحَّتِه', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'سُورة هود:', '', 'مَكِّيَّة', '', 'مَدَنيَّة', '', 'نَزَلَ نِصفُها بمَكَّة ونِصفُها في المَدينة', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'تَفسير قَولِه تَعالَى: {وَاللَّهُ عَلَى كُلِّ شَيْءٍ وَكِيلٌ}:', '', 'أنَّ اللَّهَ هو الَّذي بَدَأ الخَلْقَ', '', 'أنَّ اللَّهَ على كُلِّ شَيءٍ قَيِّمٌ، وبيدِه تَدبيرُ الأُمورِ', '', 'أنَّ اللَّهَ جامِعُ النَّاسِ ليومٍ لا رَيبَ فيه', '', '', 'b', 'easy');
