(@tafseer_category_id, '', 'سُورةُ يوسُفَ:', '', 'مَدَنيَّةٌ', '', 'مَكِّيَّةٌ', '', 'نَزَلَت آياتٌ مِنها بمَكَّة وآياتٌ مِنها بالمَدينة', '', '', 'b', 'easy');
