(@tafseer_category_id, '', 'قالَ تَعالَى على لِسانِ يوسُفَ: {وَقَدْ أَحْسَنَ بِي إِذْ أَخْرَجَنِي مِنَ السِّجْنِ}، ولَمْ يَقُلْ: مِنَ الجُبِّ:', '', 'حِفاظًا لِلأدَبِ مَعَ إخوتِه', '', 'للدَّلالةِ على شِدَّةِ ابتلائِه فيه', '', 'إشارةً إلى الفَرجِ الذي تَعقَّبَهُ', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قَولِه تَعالَى: {قَالُوا يَا أَيُّهَا الْعَزِيزُ} خاطَبوه بما يَليقُ بالأكابِرِ:', '', 'خَوفًا مِنه', '', 'لِيَرِقَّ لَهُم', '', 'لعادةِ الناسِ يَومئذٍ', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'مَعنَى (تُفَنِّدونَ) في قَولِه تَعالَى: {وَلَمَّا فَصَلَتِ الْعِيرُ قَالَ أَبُوهُمْ إِنِّي لَأَجِدُ رِيحَ يُوسُفَ لَوْلَا أَنْ تُفَنِّدُونِ}:', '', 'تُسَفِّهوني', '', 'تُصدِّقُوني', '', 'تُكذِّبُوني', '', '', 'a', 'medium');
