(@tafseer_category_id, '', 'قَولُه تَعالَى: {إِنَّا أَنْزَلْنَاهُ قُرْآنًا عَرَبِيًّا لَعَلَّكُمْ تَعْقِلُونَ} فيه رَدٌّ على:', '', 'مَن زَعَمَ أنَّ القُرآنَ فيه مُحْكَم ومُتَشابِه', '', 'مَن زَعَمَ أنَّ القُرآنَ كُلَّه مُحْكَم', '', 'مَن زَعَمَ أنَّ في القُرآنِ ما لا يُفْقَهُ ولا يُفهَم', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'مَعنَى (كَظيمٌ) في قَولِه تَعالَى: {وَابْيَضَّتْ عَيْنَاهُ مِنَ الْحُزْنِ فَهُوَ كَظِيمٌ}:', '', 'مُمسِك على الحُزنِ', '', 'مَريضٌ مَرضًا شَديدًا', '', 'كَفيفُ البَصرِ', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'يُستَفادُ مِن قِصَّة يوسُفَ:', '', 'أنَّ مَن كادَ غَيره كَيدًا مُحَرَّمًا فَإنَّ اللَّهَ تَعالَى لا بُدَّ أن يَكيدَه', '', 'أنَّ العَدلَ بين الأَبناءِ في المَحبَّة واجبٌ شَرعيٌّ', '', 'أنَّ بعدَ شِدَّةِ البَلاءِ التَّمكينَ في الأَرضِ', '', '', 'a', 'medium');
