(@tafseer_category_id, '', 'مَعنَى (يَرتَعْ) في قَولِه تَعالَى: {أَرْسِلْهُ مَعَنَا غَدًا يَرْتَعْ وَيَلْعَبْ}:', '', 'يَأكُلْ ويَنعَمْ', '', 'يَلهُو', '', 'يَصحبُنا', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَوله تعالى: {يَلْتَقِطْهُ بَعْضُ السَّيَّارَةِ}:', '', 'أصلٌ في أحكامِ اللَّقيطِ', '', 'أصلٌ في أحكامِ الدِّيةِ', '', 'أصلٌ في أحكامِ الرِّكاز', '', '', 'a', 'medium');
