(@tafseer_category_id, '', 'جَعل اللَّه رُؤيا يوسُفَ:', '', 'إشارة لِأبيه بما سيَحدُث لِوَلَدِه مِن بعدهِ', '', 'تَحذيرًا لإخوةِ يوسفَ مِن عاقِبَة الظُّلمِ', '', 'تَنبيهًا ليوسُفَ عَلَيه السَّلامُ بعُلُوِّ شَأْنِه', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'دَلَّ قَوله تَعالَى: {إِنَّهُ مَنْ يَتَّقِ وَيَصْبِرْ فَإِنَّ اللَّهَ لَا يُضِيعُ أَجْرَ الْمُحْسِنِينَ} على:', '', 'أنَّ التَّقوَى أبلغُ مِن الصَّبرِ لذلك بدأَ بها', '', 'فَضيلةِ التَّقوَى والصَّبرِ، وأنَّ كُلَّ خَيرٍ في الدُّنيا والآخِرة مِن آثارِ التَّقوَى والصَّبرِ', '', 'أنَّ الإِحسانَ لا يكونُ إلا بتَحقيقِ الصَّبرِ والتَّقوى', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'دَلَّ قَوله تَعالَى: {وَقَالَ الْمَلِكُ إِنِّي أَرَى سَبْعَ بَقَرَاتٍ سِمَانٍ يَأْكُلُهُنَّ سَبْعٌ عِجَافٌ} على:', '', 'أنَّ الرُّؤيا الصَّادِقة للمُؤمِنينَ فقط', '', 'فَسادِ رُؤيا الكافِرِ', '', 'صِحَّة رُؤيا الكافِرِ', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'دَلَّ قَوله تَعالَى: {قُلْ هَذِهِ سَبِيلِي أَدْعُو إِلَى اللَّهِ عَلَى بَصِيرَةٍ أَنَا وَمَنِ اتَّبَعَنِي وَسُبْحَانَ اللَّهِ وَمَا أَنَا مِنَ الْمُشْرِكِينَ} على:', '', 'أنَّ أصحابَ النَّبيِّ ﷺ والمُؤمِنينَ مأمورون بتَبليغ الدَّعوة إلى اللَّهِ تعالى', '', 'وُجوبِ الأمرِ بالمَعروفِ والنَّهيِ عَنِ المُنكَرِ', '', 'أنَّ النَّبيَّ ﷺ هو المَأمورُ بالدَّعوة وتَبليغ الرِّسالة', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'أفادَ قَوله تَعالَى: {وَأَوْحَيْنَا إِلَيْهِ لَتُنَبِّئَنَّهُمْ بِأَمْرِهِمْ هَذَا وَهُمْ لَا يَشْعُرُونَ * وَجَاءُوا أَبَاهُمْ عِشَاءً يَبْكُونَ}:', '', 'أنَّ البُكاءَ لا يدلُّ على صِدقِ المَظلومِ قد يكونُ تَكلُّفًا', '', 'الحَذَرَ مِن شُؤم الذُّنوب، وأنَّ الذَّنبَ الواحِدَ يَستَتبِعُ ذُنوبًا مُتَعَدِّدةً', '', 'أنَّ الوحيَ يكونُ أيضًا لغيرِ الرُّسُلِ والأنبياءِ', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قَولِه تَعالَى: {وَأَنَّ اللَّهَ لَا يَهْدِي كَيْدَ الْخَائِنِينَ} أنَّ اللَّهَ تعالى يُعاقِبُ كُلَّ خائِن:', '', 'بِأنَّه يَستَدرِجَه لِعَذاب الآخِرة', '', 'بِأنَّه يُخلَّدُ في جَهَنَّم', '', 'أنَّه يَضِلُّ كَيده ويُبطِلُه الله تعالى ولا يَهديه لِمَقصودِه، وإنْ نالَ بَعضه', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'أشارَ اللّهُ تَعالَى بقَولِه: {وَمَا كُنْتَ لَدَيْهِمْ إِذْ أَجْمَعُوا أَمْرَهُمْ وَهُمْ يَمْكُرُونَ} إلى:', '', 'شِدَّة تَكذيب المُشرِكينَ لِلنَّبيِّ صَلَّى اللهُ عَلَيْهِ وَسَلَّمَ', '', 'صِحَّة نُبوةِ نَبيِّنا صلَّى الله عليه وسلَّم', '', 'شِدَّة كَيدِ إخوةِ يوسُفَ به', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'الحُكم في قَولِه تَعالَى: {إِنِ الْحُكْمُ إِلَّا لِلَّهِ عَلَيْهِ تَوَكَّلْتُ وَعَلَيْهِ فَلْيَتَوَكَّلِ الْمُتَوَكِّلُونَ}:', '', 'الحُكم الدِّينيُّ', '', 'الحُكم الكَونيُّ', '', 'الحُكم الشَّرعيُّ', '', '', 'b', 'medium');
