(@tafseer_category_id, '', 'قوله تعالى: {إِنَّا أَنْزَلْنَاهُ قُرْآنًا عَرَبِيًّا لَعَلَّكُمْ تَعْقِلُونَ} لـ:', '', 'لتَحدِّي فُصحاءِ العَربِ أن يأتُوا بمثلِه', '', 'لِيَعلَمَ العَرَبُ والجَميعُ أنَّ نَبيَّنا مُحَمَّدًا ﷺ لَمْ يَتَلَقَّ ذلك القَصَص مِن أحَدٍ مِنَ العَرَبِ', '', 'لبَيانِ أنه ليس في القُرآنِ كلامٌ  أَعجمِيٌّ', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَوله تَعالَى: {وَالْمَلَائِكَةُ يَدْخُلُونَ عَلَيْهِمْ مِنْ كُلِّ بَابٍ * سَلَامٌ عَلَيْكُمْ بِمَا صَبَرْتُمْ}، إشارةٌ إلى أنَّ مِنَ المَلائِكة مَلائِكةً:', '', 'يُوالونَ أهلَ الجَنَّة', '', 'مُوكَّلينَ بتَحيةِ أهلِ الجَنَّة', '', 'سَيَّاحونَ في الجَنَّة', '', '', 'b', 'easy');
