(@tafseer_category_id, '', 'في قَولِه تَعالَى: {وَإِنْ تَعْجَبْ فَعَجَبٌ قَوْلُهُمْ أَإِذَا كُنَّا تُرَابًا أَإِنَّا لَفِي خَلْقٍ جَدِيدٍ أُولَئِكَ الَّذِينَ كَفَرُوا بِرَبِّهِمْ}، دِلالةٌ على:', '', 'أنَّهم لا يَعقِلونَ؛ فَإنَّ الَّذي بَدَأهم -وهو اللَّه تعالى- قادِرٌ على إعادَتِهِم', '', 'أنَّ مَن أنكَرَ المَعاد، فَإنَّه كافِرٌ برَبِّ العالَمينَ، وإنْ زَعَمَ أنَّه مُقِرٌّ بهِ', '', 'أنَّ اللَّهَ عَزَّ وجَلَّ أقامَ عَلَيهمُ الحُجَّة؛ فَلا عُذرَ لَهم في الآخِرة', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَول اللَّه تَعالَى: {سَلَامٌ عَلَيْكُمْ بِمَا صَبَرْتُمْ}، فيه إشارةٌ إلى أنَّ عِمادَ الدِّينِ كُلِّه هو:', '', 'الصَّبر', '', 'السَّلام', '', 'القُرآن', '', '', 'a', 'easy');
