(@tafseer_category_id, '', 'مَعنَى (الألباب):', '', 'القُلوب', '', 'العُقول الذَّكية', '', 'الأجسادِ', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَوله تَعالَى: {وَالَّذِينَ صَبَرُوا ابْتِغَاءَ وَجْهِ رَبِّهِمْ}، فيه إثبات صِفة:', '', 'الوَجْه لِلَّه عَزَّ وجَلَّ', '', 'العَين لِلَّه عَزَّ وجَلَّ', '', 'اليَدِ لِلَّه عَزَّ وجَلَّ', '', '', 'a', 'easy');
