(@tafseer_category_id, '', 'أفاد قولُه تعالَى: {أَلَمْ تَرَ أَنَّ اللَّهَ خَلَقَ السَّمَوَاتِ وَالْأَرْضَ بِالْحَقِّ إِنْ يَشَأْ يُذْهِبْكُمْ وَيَأْتِ بِخَلْقٍ جَدِيدٍ}:', '', 'أنهَّ تعالَى ما خلَقَ هذا العالَمَ إلَّا لداعيةِ الحِكمةِ والصَّوابِ', '', 'أنَّ خَلْقَ السماواتِ والأرضِ كان قبلَ خَلْقِ الإنسانِ', '', 'أنَّ في الكَونِ خَلقًا جَديدًا لا يعلمُ كُنهَهُ إلَّا الله سُبحانه', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'دلَّ قولُه تعالَى: {خَالِدِينَ فِيهَا بِإِذْنِ رَبِّهِمْ تَحِيَّتُهُمْ فِيهَا سَلَامٌ} على:', '', 'أنَّ السَّلامَ والخُلود دائمًا مُتلازِمان في الجنَّة', '', 'أنَّ تَحيَّةَ أهلِ الجنَّةِ لبَعضٍ وتَحيَّةَ الملائكةِ لهم سَلامٌ', '', 'أنَّ لأهلِ الجنَّةِ تَحيَّةً خاصَّةً بهم', '', '', 'b', 'medium');
