INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'أفاد قولُه تعالَى: {قُلْ لِعِبَادِيَ الَّذِينَ آمَنُوا يُقِيمُوا الصَّلَاةَ وَيُنْفِقُوا مِمَّا رَزَقْنَاهُمْ سِرًّا وَعَلَانِيَةً مِنْ قَبْلِ أَنْ يَأْتِيَ يَوْمٌ لَا بَيْعٌ فِيهِ وَلَا خِلَالٌ}:', '', 'دَعوةَ المُشرِكين أن يَدخُلوا في دينِ اللهِ قبْلَ أن يُغلَقَ بابُ التَّوبةِ', '', 'نُدبةَ أولياءِ اللهِ إلى الإقبالِ عمَّا أعرَضَ عنه أعداؤه', '', 'وَصيَّةَ المؤمنينَ ألَّا يَدَّخِروا جُهدًا في الدَّعوةِ إلى اللهِ', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'مَعنى الجزَعِ في قولِه تعالَى: {سَوَاءٌ عَلَيْنَا أَجَزِعْنَا}:', '', 'الرضا بما قَضاهُ اللهُ', '', 'الحُزنُ الذي يَصرِفُ الإنسانَ عمَّا هو بصَددِه', '', 'السَّخطُ على قدَرِ اللهِ', '', '', 'b', 'medium');
