(@tafseer_category_id, '', 'قال رسولُ اللهِ ﷺ: (إذا أرادَ اللهُ بقومٍ عذابًا أصابَ العذابُ مَن كان فيهم، ثم:', '', 'يُنجِّي اللهُ الذين آمنُوا', '', 'حاسَبَهم يومَ القيامةِ', '', 'بُعِثوا على أعْمالِهم', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'ما معنى (الزُّبُر) في قوله تعالى: {بِالْبَيِّنَاتِ وَالزُّبُرِ}:', '', 'البيِّناتِ الواضحةِ', '', 'المُعجِزات', '', 'الكتُبِ', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قوله: {الَّذِينَ صَبَرُوا وَعَلَى رَبِّهِمْ يَتَوَكَّلُونَ} المقصودُ هم:', '', 'الأنصارُ', '', 'مَن صبَر من المسلمينَ', '', 'المُهاجِرونَ', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'رُوحُ القُدسِ هو:', '', 'إسرافيلُ عليه السلامُ', '', 'جبريلُ عليه السلامُ', '', 'ملَكُ الموتِ عليه السلامُ', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قولُه: {وَاللَّهُ جَعَلَ لَكُمْ مِنْ بُيُوتِكُمْ سَكَنًا}: فيه تقديمُ (لَكُمْ):', '', 'للإيذانِ بأنَّه لمصلحتِهم ومنفعتِهم ولتشويقِ النفْسِ إلى وُرودِه', '', 'للدَّلالةِ على التَّخصيصِ الحاصِل مِن الحَصرِ بالتَّقديمِ والتَّأخيرِ', '', 'لمُراعاةِ النَّظْمِ البَياني', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قولُه تعالى: {وَالْخَيْلَ وَالْبِغَالَ وَالْحَمِيرَ لِتَرْكَبُوهَا وَزِينَةً وَيَخْلُقُ مَا لَا تَعْلَمُونَ}،
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قولِه تعالى: {خَلَقَ الْإِنْسَانَ مِنْ نُطْفَةٍ فَإِذَا هُوَ خَصِيمٌ مُبِينٌ} دليلٌ على أن التكبُّرَ متولِّدٌ في الإنسانِ من:', '', 'قلةِ معرفتِه بنفْسِه، وفكرِه فيما خُلِقَ منه', '', 'النَّفسِ الأمَّارةِ بالسُّوءِ', '', 'الشيطانِ الذي يغْويه ويجعلُه فَخُورًا بنَفسِه', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قولُه: {وَاللَّهُ خَلَقَكُمْ ثُمَّ يَتَوَفَّاكُمْ}، فيه ابتداءُ الجملة باسم الجلالةِ؛ :', '', 'لأنَّ الجُملةَ الإسمِيَّة أقوَى الجُمَل', '', 'لأنَّ دلالةَ الاسمِ العَلَم أوضَحُ وأصرحُ', '', 'لتَربيَةِ المَهابَةِ في النُّفُوس', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قولُه: {قُلُوبُهُمْ مُنْكِرَةٌ}، أي:', '', 'قلوبُهم مُغْلقةٌ', '', 'جاحِدةٌ بما هو واقعٌ', '', 'قلوبُهم مَطمُوسٌ عليها', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'معنى {أَمْوَاتٌ غَيْرُ أَحْيَاءٍ} في قوله: {أَمْوَاتٌ غَيْرُ أَحْيَاءٍ وَمَا يَشْعُرُونَ أَيَّانَ يُبْعَثُونَ}:', '', 'أنَّ قُلوبَ الكافِرينَ ماتتْ وهُم ما زالوا أَحياءَ فلا يَعقِلونَ', '', 'أنَّ الأصنامَ التي يعبُدها المشركونَ جماداتٌ لا أرواحَ فيها، فلا تسمَعُ ولا تُبصرُ ولا تعقِلُ شيئًا، فكيف تُتخَذُ هذه آلهةً؟!', '', 'أنَّ الكفَّارَ مهْما تمتَّعوا بالدُّنيا فَهُم في حُكم الأمواتِ وغيرِ الأحْياءِ', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'تقديمُ الجارِّ والضميرِ في قوله: {وَبِالنَّجْمِ هُمْ يَهْتَدُونَ}:', '', 'للتخصيصِ', '', 'للتعميمِ', '', 'للتوضيحِ', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قولُه: {لَهُمْ فِيهَا مَا يَشَاؤُونَ} تقديمُ كلمة (فيها):', '', 'للاحترازِ عن توهُّمِ تعلُّقِه بالمشيئةِ', '', 'للزِّيادةِ في تَوصِيف حالةِ النَّعيم', '', 'للحصرِ والتَّخصِيص', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قولُه: {لِلَّذِينَ لَا يُؤْمِنُونَ بِالْآخِرَةِ مَثَلُ السَّوْءِ} فيه إضافةُ المثَلِ إلى السَّوءِ:', '', 'للتَّمييزِ', '', 'للتَّأكيدِ', '', 'للبَيانِ', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'معنى (مواخِر) في قوله: {وَتَرَى الْفُلْكَ مَوَاخِرَ فِيهِ}:', '', 'جوارٍ تشُقُّ الماءَ مع صوتٍ', '', 'مُستَقِرَّة على ظَهرِه لا تَغرَق', '', 'كثيرةٌ في الماءِ', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قولُه: {أَتَى أَمْرُ اللَّهِ فَلَا تَسْتَعْجِلُوهُ} عبَّر عن الساعةِ بأمرِ اللهِ:', '', 'لأنَّه مُتحقِّقٌ لا محالةَ', '', 'للتفخيمِ والتهويلِ', '', 'للتخويفِ', '', '', 'b', 'medium');
