(@tafseer_category_id, '', 'معنى {وَمَا يَشْعُرُونَ أَيَّانَ يُبْعَثُونَ}:', '', 'وما تَدْري هذه الأصنامُ متى يَبعثُها اللهُ، فكيف يُرْتجى عندَ هذه نفعٌ أو ثوابٌ وجزاءٌ', '', 'وما يشعُرُ الكفارُ بأنَّهم سوف يُبعثونَ لأنَّهم يُنكِرُون البَعثَ بعدَ المَوتِ', '', 'وما يَعرِفُ الناسُ متى يُبعثونَ للحِساب', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'اتصلَ قولُه: {سُبْحَانَهُ وَتَعَالَى عَمَّا يُشْرِكُونَ} باستعجالِهم:', '', 'لأنَّ خوْفَهم أدَّى بهم إلى الاستعجالِ', '', 'لأنَّ استِعجالَهم استهزاءٌ وتكذيبٌ، وذلك من الشركِ', '', 'لأنَّهم في غَفلةٍ عَمَّا هُم فيه', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قولُه: {قَالَ الَّذِينَ أُوتُوا الْعِلْمَ إِنَّ الْخِزْيَ الْيَوْمَ وَالسُّوءَ عَلَى الْكَافِرِينَ} إيرادُ الظرف (اليومَ):', '', 'للإشعارِ بأنَّ التوبةَ معروضةٌ في الدُّنيا حتى المماتِ', '', 'للإشعارِ بتحقُّقِ وقوعِ ذلك اليومِ لا محالةَ', '', 'للإشعارِ بأنَّهم كانوا قبلَ ذلك في عزَّةٍ وشقاقٍ', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'جملةُ {يَقُولُونَ سَلَامٌ عَلَيْكُمْ} المَقصُود بالسَّلام هُنا:', '', 'سَلامُ حالٍ', '', 'سَلامُ تَأنِيسٍ وإكرامٍ', '', 'سَلامٌ مِن العيوبِ', '', '', 'b', 'hard');
