(@tafseer_category_id, '', 'قوله تعالى: {إِنَّ إِبْرَاهِيمَ كَانَ أُمَّةً} أي:', '', 'إنَّ إبراهيمَ لم يكنْ على ظهرِ الأرضِ مسلمٌ غيرُه', '', 'إنَّ إبراهيمَ كان أبًا للأنْبياءِ', '', 'إنَّ إبراهيمَ كان معلِّمًا جامعًا لخصالِ الخيرِ، يُقتَدى به', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قولُ اللهِ تعالى: {يُنْبِتُ لَكُمْ بِهِ الزَّرْعَ وَالزَّيْتُونَ وَالنَّخِيلَ وَالْأَعْنَابَ وَمِنْ كُلِّ الثَّمَرَاتِ}، أتى بلفظِ (مِن) التي للتبعيضِ؛ لأنَّ:', '', 'كلَّ الثمراتِ لا تكونُ إلا في الجنةِ، وإنَّما أُنبِت في الأرضِ بعضٌ من كلِّها للتذكرةِ', '', 'الناسَ تختلِفُ شهواتُهم في الأكْلِ', '', 'الناسَ لا ينتفعونَ بكلِّ الثمراتِ', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قولُ اللهِ تعالى: {وَتَسْتَخْرِجُوا مِنْهُ حِلْيَةً تَلْبَسُونَهَا} فيه دليلٌ على:', '', 'أنَّ التَّزيُّن بالحُلي مُباحٌ للنِّساء مُحرَّم على الرِّجال', '', 'إباحةِ لُبسِ النساءِ والرجالِ حليةَ البحرِ من الجواهِرِ ونحوِها', '', 'الحثِّ على السَّعيِ لاستخراجِ ما في باطِنِ البحرِ', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قُدِّمَ الأمنُ على الطُّمأنينةِ في قولِه: {آمِنَةً مُطْمَئِنَّةً}:', '', 'للتَّخصِيص والحَصر', '', 'لمُراعاةِ فَواصِل الآياتِ', '', 'إذ لا تحصُلُ الطمأنينةُ بدونِه', '', '', 'c', 'hard');
