(@tafseer_category_id, '', 'في قولِه تعالى: {الَّذِينَ تَتَوَفَّاهُمُ الْمَلَائِكَةُ طَيِّبِينَ يَقُولُونَ سَلَامٌ عَلَيْكُمُ ادْخُلُوا الْجَنَّةَ بِمَا كُنْتُمْ تَعْمَلُونَ} دليلٌ على:', '', 'نَعيمِ القَبرِ', '', 'حِسابِ القَبرِ', '', 'عَذاب القَبرِ', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قولِه: {وَاللَّهُ يَعْلَمُ مَا تُسِرُّونَ وَمَا تُعْلِنُونَ}:', '', 'تَنبِيه إلى التَّقوَى', '', 'تعريضٌ بالتهديدِ والوعيدِ', '', 'إثباتٌ لاسمِ الله العِليم', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قولُه: {ثُمَّ لَا يُؤْذَنُ لِلَّذِينَ كَفَرُوا} أي:', '', 'ثم لا يُعْطى الإذنَ للذين كَفروا للاعتذارِ إلى اللهِ من كُفرِهم', '', 'ثم لا يؤذَنُ للكافرينِ بالتوْبةِ', '', 'ثم لا يؤذَنُ للكافرينَ بدخولِ الجنَّةِ', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قولُه: {أَمْوَاتٌ غَيْرُ أَحْيَاءٍ وَمَا يَشْعُرُونَ أَيَّانَ يُبْعَثُونَ}، جملةُ (غيرُ أحياءٍ) تأكيدٌ لمضمونِ جملةِ (أمواتٌ)؛ للدلالةِ على:', '', 'تقريعِ الكفارِ بأنَّهم يَعبُدونَ الأمواتَ', '', 'وصفَ الموتَ فيهم بأنه ليس فيهم شائبةُ حياةٍ؛ لأنَّهم حجارةٌ', '', 'أنَّ الأصنامَ ميتةٌ في الأصلِ، ولم يكنْ لها في يومٍ من الأيامِ عراقةٌ', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قوله تعالى: {إِلَهُكُمْ إِلَهٌ وَاحِدٌ} هذا الخطابُ موجَّهٌ:', '', 'لكلِّ الناسِ', '', 'للكفارِ', '', 'للمؤمنينَ', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قولُه: {الَّذِينَ اتَّقَوْا وَالَّذِينَ هُمْ مُحْسِنُونَ} فيه تقديمُ التَّقوى على الإحسانِ؛ لأنَّ:', '', 'الإحسانَ أعمُّ من التَّقوى', '', 'تحقيقَ التَّقوى أسهلُ من تحقيقِ الإحسانِ', '', 'التخليةَ متقدمةٌ على التحليةِ', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قولُه: {ضَرَبَ اللَّهُ مَثَلًا عَبْدًا مَمْلُوكًا..} فائدةُ ذِكرِه (مملوكًا) بعد قولِه: (عبدًا):', '', 'الاحترازُ عن الحرِّ فإنَّه عبدٌ للهِ تعالى، وليس مملوكًا لغيرِه', '', 'لتَحقيرِ حالةِ المَملُوكِيَّة', '', 'للدَّلالةِ على انغِماسِه في الرِّقِّ ورِضاه بِه', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'لم يُعبَّرْ عن جهنَّمَ بالدارِ كما عبَّرَ عن الجنَّةِ في قولِه تعالى: {وَلَنِعْمَ دَارُ الْمُتَّقِينَ}:', '', 'لتَميِيز فَريقِ المُؤمِنين', '', 'لأنَّهم يتقلَّبونَ فيها ولا يَستقِرونَ', '', 'تحقيرًا لهم', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قوله تعالى: {أَنْ أَنْذِرُوا أَنَّهُ لَا إِلَهَ إِلَّا أَنَا فَاتَّقُونِ} خطابٌ على طريقةِ:', '', 'العطفِ', '', 'الالتفاتِ', '', 'التعقيبِ', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'أتى رسولَ اللهِ ﷺ فقال له: يا رسولَ اللهِ؛ ما تُرِكتُ حتى نلتُ منكَ، وذكَرتُ آلهتَهم بخيرٍ، قال: (كيف تجِدُ قلبَكَ؟) قال: مطمئنًا بالإيمانِ). ذلك هو:', '', 'عمَّارُ بنُ ياسرٍ رضيَ اللهُ عنه', '', 'بلالٌ رضيَ اللهُ عنه', '', 'صُهيبٌ رضيَ اللهُ عنه', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'ضميرُ (نحن) في قولهم: {مَا عَبَدْنَا مِنْ دُونِهِ مِنْ شَيْءٍ نَحْنُ وَلَا آبَاؤُنَا}:', '', 'إِشارَة إلى عِبادتِهم الزَّائِفَة', '', 'للدلالةِ على أنَّهم مُصرونَ على باطلِهم', '', 'تأكيدٌ للضميرِ المتصِلِ في (عَبَدْنَا)', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قولُه: {أَفَغَيْرَ اللَّهِ تَتَّقُونَ} استفهامٌ تضمنَ:', '', 'التوبيخَ والتعجبَ', '', 'النَّفيَ والإِنكارَ', '', 'التَّأكيدَ', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قوله تعالى: {وَمِنْهُ شَجَرٌ فِيهِ تُسِيمُونَ} مَعنى (تُسِيمُونَ):', '', 'تَأكُلون مِنه ثَمَرًا', '', 'تُوقِدُون نارًا', '', 'تَرعَوْنَ أنعامَكم', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قوله تعالى: {تَاللَّهِ لَتُسْأَلُنَّ عَمَّا كُنْتُمْ تَفْتَرُونَ} صَرْف الكلام:', '', 'مِن الغَيْبة إلى الخطابِ', '', 'مِن الخَطابِ إلى الضَّمِير', '', 'مِن الضَّميرِ إلى الغَيْبَة', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'النَّهيُ عنِ التَّكلُّمِ بغَيرِ عِلمٍ في قولِه تعالَى: {وَلَا تَقْفُ مَا لَيْسَ لَكَ بِهِ عِلْمٌ}:', '', 'خاصٌّ بالعِلمِ الدُّنيويِّ', '', 'نهيٌ عامٌّ في جميعِ أنواعِ الأخبارِ', '', 'خاصٌّ بالعِلمِ الشَّرعيِّ', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'دلَّ قولُه تعالَى: {وَمَا مَنَعَنَا أَنْ نُرْسِلَ بِالْآيَاتِ إِلَّا أَنْ كَذَّبَ بِهَا الْأَوَّلُونَ} على:', '', 'أنَّ جَميعَ الأُمَمِ مُتساويَة عندَ الله تعالى', '', 'أنَّ كلَّ الأَنبياءِ كانت لهم مُعجِزات', '', 'تَشريفِ هذه الأُمَّةِ وتَفضيلِ رَسولِها على سائرِ الرُّسلِ', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'من مَوضوعاتِ سورة الإسراءِ:', '', 'ذِكرُ الإسراءِ وبَيانُ حِكمتِه، والإشارةُ إلى المِعراجِ', '', 'إقامةُ الأدِلَّةِ على وَحدانيَّةِ اللهِ تعالى، وعلى شُمولِ قُدرتِه', '', 'الإنذارُ بالبَعثِ، وتحقيقُ وُقوعِه', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'معنى (رُفاتًا) في قولِه تعالَى: {وَقَالُوا أَإِذَا كُنَّا عِظَامًا وَرُفَاتًا}:', '', 'مَوتى', '', 'تُرابٌ وفُتاتٌ', '', 'حِجارةٌ', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'سورة الإسراءِ:', '', 'مَدنيَّةٌ', '', 'نزَل مُعظَمُها في مكَّة، ونزلَت آياتٌ منها بالمدينةِ', '', 'مكِّيةٌ', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'معنى (دُلوك) في قولِه تعالَى: {أَقِمِ الصَّلَاةَ لِدُلُوكِ الشَّمْسِ}:', '', 'زَوالٌ', '', 'طُلوعٌ', '', 'ظُهورٌ', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'دلَّ قولُه تعالَى: {فَلَا يُسْرِفْ فِي الْقَتْلِ إِنَّهُ كَانَ مَنْصُورًا} على:', '', 'أنَّ الإسرافَ مَنهِيٌّ عنه', '', 'أنَّه لا يَجوزُ لوليِّ المَقتولِ أنْ يَتجاوَز الحدَّ المشروعَ له', '', 'أنَّ القَتلَ الخَطأَ فيه القَصاصُ والدِّيَة', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'مِن أَسماءِ سُورةِ الإسراءِ:', '', 'سُورةُ السَّجدَتَينِ', '', 'سُورةُ النِّعَم', '', 'سُورةُ بَنِي إِسرائيلَ', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'معنى (منشورًا) في قولِه تعالَى: {وَنُخْرِجُ لَهُ يَوْمَ الْقِيَامَةِ كِتَابًا يَلْقَاهُ مَنْشُورًا}:', '', 'مَفتوحًا', '', 'مَطوِيًّا', '', 'مَحفوظًا', '', '', 'a', 'easy');
