(@tafseer_category_id, '', 'دلَّ قوله تعالَى: {وَقُرْآنَ الْفَجْرِ إِنَّ قُرْآنَ الْفَجْرِ كَانَ مَشْهُودًا} على أنَّ القِراءةَ في الصَّلاةِ:', '', 'رُكنٌ', '', 'مُستحَبَّةٌ', '', 'واجِبَة', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'تَضمَّن قولُه تعالَى: {وَكَانَ الْإِنْسَانُ عَجُولًا}:', '', 'الحَثَّ على التُّؤدَة', '', 'بَيانَ خَلقِ الإنسانِ', '', 'ذمَّ العجَلةِ', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'معنى قولِه تعالَى: {الَّذِي بَارَكْنَا حَوْلَهُ}:', '', 'أرسَلْنا فيه الرُّسُلَ', '', 'حفَفْناه بالمَلائكةِ', '', 'جَعَلْنا حَولَه البَرَكةَ', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'من مَقاصدِ سورةِ الإسراءِ:', '', 'تَرسيخُ أُصول العَقيدةِ الإسلاميَّةِ، وتَنقيتُها من كلِّ ما يَشوبُها', '', 'الدَّعوةُ إلى التعاونِ على البِرِّ والتَّقوى', '', 'الأمرُ بالجهادِ لرفعِ رايةِ الإسلامِ', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'معنى قولِه تعالَى: {وَلَتَعْلُنَّ عُلُوًّا كَبِيرًا}:', '', 'تَرتفعُ مَنزلتُكم عندَ اللهِ إذا التَزمتُم بالتَّقوَى', '', 'تَستكبِرون على اللهِ بمُخالَفةِ أمْرِه، والتجبُّرِ على عِبادِه استِكبارًا وطُغيانًا شديدًا', '', 'تَعلو مَنزلتُكم بالعلمِ والعَمَلِ الصَّالِحِ', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'أفاد قولُه تعالَى: {وَكَفَى بِرَبِّكَ بِذُنُوبِ عِبَادِهِ خَبِيرَا بَصِيرًا}:', '', 'إِثباتَ صِفَة القُدرةِ لله تعالى', '', 'أنَّ المَغفِرةَ لا تكونُ عَطاءً بلا سَعيٍ', '', 'أنَّ الذُّنوبَ هي ِ أسبابُ الهَلَكةِ', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'معنى (لوليِّه) في قولِه تعالَى: {فَقَدْ جَعَلْنَا لِوَلِيِّهِ سُلْطَانًا}:', '', 'مَن تَربِطُهم به صِلةُ قَرابةٍ من عَصَبَتِه', '', 'مَن يَلي أمْرَه من ورَثتِه إنْ كانوا موجودين', '', 'مَن يَكبُره سِنًّا من إخوتِه', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'الأوَّابُ في قولِه تعالَى: {فَإِنَّهُ كَانَ لِلأَوَّابِينَ غَفُورًا}:', '', 'كَثيرُ الأَوْبِ؛ أي: الصَّدقَة', '', 'الذي مِن عادتِه الرُّجوعُ إلى اللهِ تعالَى', '', 'الذي كَثُرت ذُنوبُه', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'أفاد قولُه تعالَى: {وَجَعَلْنَا عَلَى قُلُوبِهِمْ أَكِنَّةً أَنْ يَفْقَهُوهُ}:', '', 'التَّحذيرَ مِن النِّفاقِ وأَهلِه', '', 'الحثَّ على فِقه القُرآنِ وتَعلُّم معناه', '', 'تَنبيهَ المُؤمنِينَ إلى تَزكيَة قُلوبِهم', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'أومَأ قولُه تعالَى: {وَإِنْ كَادُوا لَيَسْتَفِزُّونَكَ مِنَ الْأَرْضِ لِيُخْرِجُوكَ مِنْهَا وَإِذًا لَا يَلْبَثُونَ خِلَافَكَ إِلَّا قَلِيلًا} إلى:', '', 'أنَّ اللهَ تعالَى سيَنصُرُ رسولَه على أهلِ مكَّة، فلا يستطيعون أنْ يُخرِجوه منها', '', 'أنَّ النبيَّ ﷺ سيُشيِّد دعائمَ أركانِ الإسلام في مكَّةَ', '', 'أنَّ الرَّسولَ سيَخرُجُ مِن مكَّةَ وأنَّ المُتسَبِّبينَ في خُروجِه لا يَلبَثونَ بَعدَه بمكَّةَ إلَّا قَليلًا', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'معنى (تَركَن) في قولِه تعالَى: {وَلَوْلَا أَنْ ثَبَّتْنَاكَ لَقَدْ كِدْتَ تَرْكَنُ إِلَيْهِمْ شَيْئًا قَلِيلًا}:', '', 'تَطمَئنُّ', '', 'تَدعُو', '', 'تَتَوَجَّه', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'دلَّ قولُه تعالَى: {وَمَا كُنَّا مُعَذِّبِينَ حَتَّى نَبْعَثَ رَسُولًا} على:', '', 'أنَّ العَذابَ لا يكونُ إلَّا للمُشرِكين', '', 'أنَّ أَهلَ الفَترةِ حُكمُهُم حُكمُ أَهلِ الأَعرافِ', '', 'أنَّ الله لا يُعذِّب أحدًا حتى تقومَ عليه الحُجَّةُ بالرُّسُلِ', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'أفاد قولُه تعالَى: {رَبُّكُمْ أَعْلَمُ بِكُمْ إِنْ يَشَأْ يَرْحَمْكُمْ أَوْ إِنْ يَشَأْ يُعَذِّبْكُمْ}:', '', 'أنَّ رحمةَ اللهِ وعذابَه صِفاتٌ لا إراديَّةٌ', '', 'أنَّ الرحمةَ والتعذيبَ مُعَلَّقانِ بالمَشيئةِ', '', 'أنَّ اللهَ تعالَى لا يَرحَمُ مَن يَستَحِقُّ العذابَ', '', '', 'b', 'medium');
