(@tafseer_category_id, '', 'معنى (مَحسورًا) في قولِه تعالَى: {فَتَقْعُدَ مَلُومًا مَحْسُورًا}:', '', 'مَذمومًا', '', 'مُنقَطِعًا عنِ النَّفقةِ', '', 'مُسرِفًا', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'الفَرقُ بين المَذمومِ والمَلُومِ في قولِه تعالَى: {فَتَقْعُدَ مَلُومًا مَحْسُورًا} و{فَتَقْعُدَ مَذْمُومًا مَخْذُولًا}:', '', 'المذمومُ: أنْ يذكُرَ أنَّ الفعلَ الَّذي أقدَمَ عليه قَبيحٌ مُنكَرٌ. والملومُ: أن يُقال لفاعلِه: لِم فعلْتَ كذا؟', '', 'المَذمُوم: ما لَحِقَه إِفراطٌ. والمَلُوم ما لَحِقَهُ تَفرِيط', '', 'المَذمُوم: كلُّ ما تَعلَّقَت به الحَسرةُ، والمَلُوم: كلُّ ما كان بِلا نَفعٍ', '', '', 'a', 'hard');
