(@tafseer_category_id, '', 'قَولُه تَعالَى: {وَلَمْ تَرْقُبْ} مَعنَى {تَرْقُبْ}', '', 'تَعلَمُ وتَعرِفُ', '', 'تُرْاعِي وتحفَظُ', '', 'تُرسِلُ مَن يُراقِبُ', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قولُ اللهِ تَعالَى: {وَمَا تِلْكَ بِيَمِينِكَ يَا مُوسَى * قَالَ هِيَ عَصَايَ أَتَوَكَّأُ عَلَيْهَا وَأَهُشُّ بِهَا عَلَى غَنَمِي وَلِيَ فِيهَا مَآَرِبُ أُخْرَى} يُستفادُ منه:', '', 'أنَّ التَّوَكُّؤَ كان مِن سُنَنِ الأَنبِياءِ', '', 'جَوازُ الزِّيادةِ في الجَوابِ على ما في السُّؤالِ', '', 'أن للعَصاةِ فَوائِدَ كَثيرةً', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قَولِه تَعالَى: {وَقَتَلْتَ نَفْسًا} المَقتُولُ هُنا هو:', '', 'الرَّجُلُ القبطيُّ من آلِ فِرعَونَ', '', 'ساحِرٌ مِن بَنِي إِسرائِيلَ', '', 'رَجُلٌ مِن أَهلِ مَدْينَ', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَولُه تَعالَى: {إِذْ رَأَى نَارًا فَقَالَ لِأَهْلِهِ امْكُثُوا إِنِّي آَنَسْتُ نَارًا} سافَرَ مُوسَى عَليهِ السَّلامُ في لَيلةٍ بارِدةٍ ظَلْماءَ من:', '', 'مَدْينَ إلى بَيتِ المَقدِسِ', '', 'مَدْينَ إلى مِصرَ', '', 'نِينَوى إلى العِراقِ', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَولُه تَعالَى: {وَكَذَلِكَ الْيَوْمَ تُنْسَى} مَعنَى {تُنسَى}', '', 'تُترَكُ في العَذابِ', '', 'لا تُعرَف', '', 'تُمنَع مِن الكلامِ', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قال اللهُ تَعاَلى: {وَأَنَا اخْتَرْتُكَ فَاسْتَمِعْ لِمَا يُوحَى}. قال سُفيانُ بنُ عُيَينةَ: أوَّلُ العِلمِ:', '', 'الصَّمتُ', '', 'الوَحيُ', '', 'الاستِماعُ', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قال اللهُ تَعاَلى: {إِنَّنِي أَنَا اللَّهُ لَا إِلَهَ إِلَّا أَنَا} أوَّلُ واجبٍ على المُكَلَّفينَ', '', 'الصَّلاةُ', '', 'أنْ يَعلَموا أنَّه لا إلهَ إلا اللهُ وَحْدَه لا شَريكَ له', '', 'تَعلُّمُ أَحكامِ الحَلالِ والحَرامِ', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قَولِه تَعالَى: {فَأَوْجَسَ فِي نَفْسِهِ خِيفَةً مُوسَى} الوَجْسُ هو:', '', 'التَّرَدُّد والحِيرَةُ', '', 'الفَزَعُ', '', 'الصوت الخفي', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'تَقديمُ المَجرورِ في قولِه تَعالَى: {لَهُ الْأَسْمَاءُ الْحُسْنَى}', '', 'للاختِصاصِ', '', 'للعُمومِ', '', 'للتَّقريرِ', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قولُ اللهِ تَعالَى: {فَلَا يَصُدَّنَّكَ عَنْهَا مَنْ لَا يُؤْمِنُ بِهَا وَاتَّبَعَ هَوَاهُ فَتَرْدَى} فيه إشارةٌ إلى:', '', 'أنَّ الباعِثَ الوَحيدَ على الكُفرِ هو الهَوَى', '', 'أنَّ كلَّ صَدٍّ عن سَبيلِ الله فهو مِن إِبليسَ', '', 'أنَّ مَن ترَكَ المُراقبةَ لَحظةً حادَ عنِ الدَّليلِ، ومَن حادَ عنِ الدَّليلِ هَلَك', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قال اللهُ تَعاَلى: {قَالَ مَوْعِدُكُمْ يَوْمُ الزِّينَةِ وَأَنْ يُحْشَرَ النَّاسُ ضُحًى} إنَّما واعَدَهم ذلك اليَومَ', '', 'لأنَّه كان اليومُ الذي لا يَخرُج فيه فِرعونُ، فأرادَ كَيْدَهُ بإخْراجِهِ', '', 'لِيَكونَ عُلُوُّ كلمةِ اللهِ، وظُهورُ دينِه،  على رُؤوسِ الأشهادِ', '', 'لأنَّه يومُ خُلُوِّ الجَميعِ مِن أَعمالِهم', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قولُه تَعالَى: {وَإِنْ تَجْهَرْ بِالْقَوْلِ فَإِنَّهُ يَعْلَمُ السِّرَّ وَأَخْفَى} طابَقَ الجَزاءُ الشَّرطَ', '', 'نهْيًا عن الجَهْرِ', '', 'للرِّعايَةِ على الفاصِلَةِ', '', 'تَرغِيبًا في الجَزاءِ', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قال تَعالَى: {وَقَدْ آَتَيْنَاكَ مِنْ لَدُنَّا ذِكْرًا} سُمِّيَ القُرآنُ بالذِّكرِ لأنَّه:', '', 'كِتابٌ ذَاكِرٌ؛ بمعنَى مَذكُور أي: مَتلُو', '', 'يَذكُرُ حَالَ المُؤمِنينَ وحَالَ الكافِرينَ في الدُّنيا ومَآلَهُم جَميعًا في الآخِرَةِ', '', 'يُذَكِّرُ أنواعَ آلاءِ اللهِ تَعالَى ونَعمائِه؛ ففيه التَّذكيرُ والمواعِظُ', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَولُه تَعالَى: {فَيَذَرُهَا قَاعًا صَفْصَفًا} مَعنَى {قَاعًا}', '', 'صَلبَةٌ', '', 'سَهلةٌ مُستويةٌ', '', 'عَميقةٌ كقاعِ البِحارِ', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قولُه تَعالَى: {كَيْ نُسَبِّحَكَ كَثِيرًا * وَنَذْكُرَكَ كَثِيرًا} قَدَّمَ التَّسبيحَ', '', 'لأنَّ التسبيحَ لما كان ذِكرًا خاصًّا؛ لكونِه بالتَّنزيهِ الذي أعْلاه التَّوحيدُ؛ أتبعَه العامَّ', '', 'لإثباتِ الارتِقاءِ مِن العالِي إلى الأَعلَى', '', 'لأنَّه تَنزيهُه تَعالَى أَحَبُّ إليه مِن التَّسبِيحِ', '', '', 'a', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'عنِ ابنِ مسعودٍ رَضيَ اللهُ عنه، قَالَ: (بَني إسرائيلَ، والكهفُ، ومريمُ، وطه، والأنبياءُ: هنَّ مِن العِتاقِ الأُوَلِ) مَعنَى العِتاقِ الأُوَلِ:', '', 'من أواخِرِ ما نَزَل منَ القُرآنِ', '', 'من أوسَطِ ما نَزَل منَ القُرآنِ', '', 'مِن السُّوَرِ المتقدِّمِ نُزولُها', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'مِن أعجَبِ ما ظاهِرُه الرَّجاءُ وهو شَديدُ التَّخويفِ، قولُه تَعالَى: {وَإِنِّي لَغَفَّارٌ لِمَنْ تَابَ وَآمَنَ وَعَمِلَ صَالِحًا ثُمَّ اهْتَدَى} :', '', 'لأنَّ الاهتِداءَ لا يكونُ إلَّا للأَنبياءِ ومَن نَحَى نَحوَهُم؛ وهُم قَلِيلٌ', '', 'لأنَّه علَّق المغفرةَ على أربعةِ شُروطٍ، يَصعُبُ تَصحيحُها', '', 'لأنَّه أضمَرَ العَذابَ في داخِلِ المَغفِرةِ', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'وفي قولِه تَعالَى: {وَأَلْقَيْتُ عَلَيْكَ مَحَبَّةً مِنِّي} نكَّرَ المحبَّةَ وأسْنَدَها إليه سُبحانَه لـ:', '', 'ما في التَّنكيرِ منَ الفَخامةِ الذَّاتيَّةِ', '', 'ما في إسنادِها إليه منَ الفَخامةِ الإضافيَّةِ', '', 'جَميعُ ما سَبَق', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'مَعنَى قَولِه تَعالَى: {أَعْطَى كُلَّ شَيْءٍ خَلْقَهُ}:', '', 'أَعطَى أَنبِياءَهُ ورُسُلَهُ ما يُثبِتُ صِدْقَ دَعوتِهم', '', 'أعْطى خَليقتَه كلَّ شيءٍ يَحتاجونَ إليه', '', 'أَعطَى خَلقَه كُلَّهم رِزقَهُم؛ مُؤمِنَهم وكافِرَهم، إِنْسَهُم وجِنَّهُم', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قولُه تَعالَى: {فَغَشِيَهُمْ مِنَ الْيَمِّ مَا غَشِيَهُمْ} فيه إبهامٌ؛ والتَّعبيرُ بالاسمِ المُبهَم الذي هو المَوصولُ يدُلُّ على', '', 'تَحقيرِ الأمرِ، ودَحْضِ شأنِه', '', 'تَعظيمِ الأمرِ، وتَفخيمِ شأنِه', '', 'التَّأكيدُ على فِعلِ الغَشَيانِ', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَولُه تَعالَى: {تَنْزِيلًا مِمَّنْ خَلَقَ الْأَرْضَ وَالسَّمَاوَاتِ الْعُلَى} يُستفادُ منه:', '', 'أنَّ "العُلَى" صِفَةٌ من صِفاتِ الله عزَّ وجلَّ ويُقصَدُ بها العُلُوُّ', '', 'تَفخيمٌ وتعظيمٌ لشأْنِ القُرآنِ', '', 'أنَّ الأرضَ بُنِيَت قبلَ السَّماواتِ', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قَولِه تَعالَى: {أَفَلَا يَرَوْنَ أَلَّا يَرْجِعُ إِلَيْهِمْ قَوْلًا وَلَا يَمْلِكُ لَهُمْ ضَرًّا وَلَا نَفْعًا} دَليلٌ عَقليٌّ سَمعيٌّ على أنَّ:', '', 'الإلهَ لا بُدَّ أن يَملِكَ لعابِدِه الضَّرَّ والنَّفعَ', '', 'الإلهَ لا بُدَّ أن يُكَلَّمَ ويتكَلَّمَ', '', 'جَميعُ ما سَبَق', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'مَعنَى {مَآرِبُ} في قَولِه تَعالَى: {وَلِيَ فِيهَا مَآرِبُ أُخْرَى}:', '', 'أسرارٌ', '', 'أَحوالٌ', '', 'حاجاتٌ', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قولُ اللهِ تَعالَى: {إِنَّنِي مَعَكُمَا أَسْمَعُ وَأَرَى} السَّمعُ والرُّؤيةُ هنا', '', 'المَقصُود بهِما القُدرَةُ', '', 'سَمْعٌ ورؤيةٌ خاصَّانِ يَقتَضِيانِ النَّصرَ والتَّأييدَ والحِمايةَ مِن فِرعَونَ', '', 'سَمْعٌ ورؤيةٌ عامَّةٌ لجَميعِ الخَلقِ', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قَولِه تَعالَى: {لَا تَخَافَا إِنَّنِي مَعَكُمَا أَسْمَعُ وَأَرَى} حُجَّةٌ على', '', 'الحَدَّاديَّةِ والسُّروريَّةِ في تَأويلِهم صِفَتَيِ السَّمعِ والبَصَرِ', '', 'المُعتَزِلةِ والجهميَّةِ في تَأويلِهم صِفَتَيِ السَّمعِ والبَصَرِ', '', 'الشِّيعةِ والخَوارجِ في تَأويلِهم صِفَتَيِ السَّمعِ والبَصَرِ', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'مَعنَى قَولِه تَعالَى: {فَإِنَّ لَكَ فِي الْحَيَاةِ أَنْ تَقُولَ ‌لَا ‌مِسَاسَ}:', '', 'تَموتَ بمَرضِ المَسَاسِ؛ وهو مَرضٌ يُصِيبُ العَقلَ بالخَبَلِ', '', 'لا تَمسُّ النَّاسَ ولا يمَسُّونَك', '', 'أي: يَعلَقُ لِسانُه ولا يَمَسُّ حَلقَهُ فلا يَتكَلَّم', '', '', 'b', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قولُه تَعالَى: {وَلِيَ فِيهَا مَآَرِبُ أُخْرَى} الإيجازُ في هذه العِبارةِ هو من:', '', 'نَظْمِ القُرآنِ لا مِن كَلامِ مُوسى عليه السَّلامُ', '', 'قَولِ مُوسى', '', 'كِلا القَولَينِ مُحتَمَلٌ', '', '', 'c', 'medium');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قولُه تَعالَى: {وَلِيَ فِيهَا مَآرِبُ أُخْرَى} لم يَقُل: «أُخَرُ»', '', 'مُراعاةً لِلفَواصِلِ', '', 'لأنَّها مُؤنَّثٌ', '', 'اتِّباعًا لِلُغةِ بَنِي إِسرائِيلَ', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قَولِه تَعالَى: {فَلَمَّا أَتَاهَا نُودِيَ يَا مُوسَى * إِنِّي أَنَا رَبُّكَ فَاخْلَعْ نَعْلَيْكَ إِنَّكَ بِالْوَادِ الْمُقَدَّسِ طُوًى} حُجةٌ على مَن يقولُ:', '', 'بخَلْقِ القُرآنِ، ويَزعُمُ أنَّ اللهَ لا يَجوزُ عَليهِ الكَلامُ', '', 'بإمامةِ عَليٍّ وأنَّه أَوْلى بالخِلافةِ من أبي بَكرٍ', '', 'بأنْ لا قَدرَ وأنَّ الأمْرَ أُنُفٌ', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قولُه تَعالَى: {وَهَلْ أَتَاكَ حَدِيثُ مُوسَى} (هل) في الاستِفهامِ تُفيدُ مَعنَى:', '', 'النَّفيِ', '', 'التَّحقيقِ', '', 'التَّقريرِ', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قَولِه تَعالَى: {اذْهَبَا إِلَى فِرْعَوْنَ إِنَّهُ طَغَى * فَقُولَا لَهُ قَوْلًا لَيِّنًا لَعَلَّهُ يَتَذَكَّرُ أَوْ يَخْشَى}:', '', 'تَحذِيرٌ مِن الغَفلَةِ التي تُودِي بصَاحِبِها إلى مُعارَضَةِ سُنَنِ الله في خَلقِه', '', 'تَنبيهٌ على نِهايةِ تَعظيمِ حَقِّ الأبَوينِ لأنَّه عليه السلامُ كان قد ربَّاه فِرعَونُ، فأمَره أن يُخاطِبَه بالرِّفقِ؛ رِعايةً لتِلك الحُقوقِ', '', 'إِشارةٌ إلى الصَّبرِ الجَميلِ على الأَذَى في سَبيلِ دِينِ الله عزَّ وجلَّ', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قال تَعالَى: {الرَّحْمَنُ عَلَى الْعَرْشِ اسْتَوَى} ولم يَقُل: «استَوَى عَلى العَرشِ»:', '', 'لإظهارِ عُلُوِّ العَرشِ', '', 'لإفادةِ الحَصرِ والتَّخصيصِ', '', 'للتَّأكيدِ على فِعلِ الاستِواءِ', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَولُه تَعالَى: {تَنْزِيلًا مِمَّنْ خَلَقَ الْأَرْضَ وَالسَّمَاوَاتِ الْعُلَا} تَمَّ تَقديمُ الأرضِ في الذِّكرِ لكَونِها:', '', 'أَكثرَ إِبداعًا للمُتَأَمِّلِ', '', 'خُلِقَت أوَّلًا', '', 'أقرَبَ إلى الحِسِّ', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قال تَعالَى: {فَأُلْقِيَ السَّحَرَةُ سُجَّدًا} ولم يَقُل: (فسَجَدوا):', '', 'لأنَّ التَّعبيرِ بفِعلِ الإلقاءِ ثم السُّجودِ آكَدُ في الفِعلِيَّة مِن التعبيرِ بالسُّجودِ وَحدَه', '', 'لإظهارِ الحالِ المُعَبِّرَةِ عن صِدقِ السُّجودِ', '', 'أُسنِدَ الفِعلُ إلى المجهولِ؛ لأنَّهم لا مُلْقيَ لهم إلَّا أنفُسُهم', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَولُه تَعالَى: {وَعَنَتِ الْوُجُوهُ لِلْحَيِّ الْقَيُّومِ} مَعنَى {الْقَيُّومِ}', '', 'مَالِكُ يَومِ القِيامَةِ ومَليكُه', '', 'القائمُ بنَفسِه، المُقيمُ لِكُلِّ مَا سِوَاهُ', '', 'الذي لا يَغِيبُ عن مُلكِه شَيءٌ', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قولُه تَعالَى: {إِذْ أَوْحَيْنَا إِلَى أُمِّكَ مَا يُوحَى * أَنِ اقْذِفِيهِ فِي التَّابُوتِ فَاقْذِفِيهِ فِي الْيَمِّ} أبْهَم في أوَّلِ الأمرِ ثُمَّ فَصَّلَه في الآيةِ بَعدَها والغَرَضُ من ذلك:', '', 'لتَفخيمِ أمْرِ المُبهَمِ وإعظامِه', '', 'للتَّخصِيصِ والحَصرِ', '', 'للتَّشوِيقِ إلى المَآلِ', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'زِيادةُ {رَبِّ} في قولِه تَعالَى: {وَعَجِلْتُ إِلَيْكَ رَبِّ لِتَرْضَى}', '', 'للاستِئناسِ بجانِبِ الرُّبُوبِيَّة', '', 'لأنَّه اسمُ اللهِ الأَعظَم الذي إذا دُعِيَ به أَجابَ وإذا سُئِلَ به أَعطَى', '', 'لمَزيدِ الضَّراعةِ والابتهالِ رغبةً في قَبولِ العُذْرِ', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'مَعنَى قَولِه تعالى: {وَأَسَرُّوا النَّجْوَى}:', '', 'أَصَرُّوا على كُفرِهِم وعِنادِهِم', '', 'تَجاذَبوا الحديثَ سِرًّا فيما بينَهم، وبالَغوا في إخفائِه مِن فِرعَونَ', '', 'أَشارَ بَعضُهم إلى بَعضٍ سِرًّ: أنِ انْجوا بأَنفُسِكُم', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَولُه تَعالَى: {لَآيَاتٍ لِأُولِي النُّهَى} النُّهَى جمعٌ، مفردُه', '', 'نُهاةٌ', '', 'نَاهِيَةٌ', '', 'نُهْيَةٌ', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قَولِه تعالى: {وَهَلْ أَتَاكَ حَدِيثُ مُوسَى} بَدَأ بمُوسَى عَليهِ السَّلامُ مع أنَّه سيَذكُرُ له أنبياءَ آخَرين:', '', 'لأنَّ المِحنةَ والفِتنةَ الحاصِلةَ له كانت أعظَمَ', '', 'لأنَّ أَخبار بني إِسرائِيلَ كانت أَشهَرَ عندَ العَربِ', '', 'لأنَّه أَقدَمُ المَذكُورِينَ وُجُودًا', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', '(تضمَّن اللهُ لِمَن قرأَ القُرآنَ، واتَّبعَ ما فيه ألَّا يَضِلَّ في الدُّنيا، ولا يَشقَى في الآخرةِ)، ثم تَلا هذه الآيةَ: {فَمَنِ اتَّبَعَ هُدَايَ فَلَا يَضِلُّ وَلَا يَشْقَى}). قائلُ هذه العِبارةِ هو:', '', 'ابنُ عبَّاسٍ', '', 'أبو سَعيدٍ الخُدرِيُّ', '', 'ابنُ مَسعودٍ', '', '', 'a', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قَولِه تَعالَى: {وَأَشْرِكْهُ فِي أَمْرِي} جاءَت قراءة: {وَأُشْرِكْهُ} بضَمِّ الهَمزةِ؛ وذلك على وَجْهِ:', '', 'الطَّلَب', '', 'الدُّعاءِ', '', 'الإخبارِ', '', '', 'c', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'قَولُه تَعالَى: {يَتَخَافَتُونَ بَيْنَهُمْ إِنْ لَبِثْتُمْ إِلَّا عَشْرًا} إذا ذُكِرَ العددُ دونَ مَعدودِه المُذَكَّرِ', '', 'حُذِفَت منه التَّاءُ', '', 'جاز فيه الوَجهانِ؛ حَذفُ التَّاءِ، وذِكرُها', '', 'أُثبِتَت فيه التَّاءُ', '', '', 'b', 'hard');
