(@tafseer_category_id, '', 'في جمع لَفظةِ: "السَّموات" في قَولِه تَعالَى: {وَلَهُ مَنْ فِي السَّمَوَاتِ} دَلالةٌ على:', '', 'تخصيصِ جِهَةِ السَّماءِ', '', 'اقتِضاءِ تَعْمِيمِ المُلْك', '', 'المبالغةِ', '', '', 'b', 'hard');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'ذَكَرَ ابن مسعود رضي الله عنه سورةَ الأنبياءِ مع بَعْضِ سُوَرِ القرآنِ الأخرى وقال: «هُنَّ مِنَ العِتَاقِ الأَوَّلِ» ما هي هِذه السُّوَرُ:', '', 'سورةُ البَقَرةِ وآلِ عمرانَ والنِّساء', '', 'سورةُ طَهَ والكَهْف ومَرْيَم', '', 'سورةُ بَنِي إسرائيلَ والكَهْفِ ومَرْيَم', '', '', 'c', 'hard');
