(@tafseer_category_id, '', 'أمَر اللهُ عزَّ وجلَّ النَّاسَ أن يتَّقوهُ، ومِن أهمِّ مظاهرِ التَّقوى:', '', 'القَناعَةُ عمَّا في أَيدِي الناسِ', '', 'تركُ الشِّركِ والفُسوقِ والعصيانِ', '', 'الزُّهدُ في المُباحاتِ', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'مِن أسماءِ نارِ جَهنَّمَ', '', 'السَّعير', '', 'السُّعُور', '', 'السَّوداءُ', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'إذا كانت كُلُّ أُمَّةٍ تَدَّعي أنَّها على الحَقِّ وغَيرَها على الباطِلِ، وتجادِلُ في ذلك فإنَّ الفصْلَ بينهم:', '', 'بما في القُرآنِ مِن حُجَجٍ دامِغَةٍ', '', 'المباهلة', '', 'يومَ القيامةِ إذ لم تُفِدْهمُ الحُجَجُ في الدُّنيا', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'جِدَال المُسْلِم مَعَ غيرِه مِن أهلِ البِدَع والمُشْرِكين في اللهِ:', '', 'يَجُوز ولكنَّه مَكرُوه', '', 'لا يَجوزُ مُطلَقًا', '', 'جائزٌ إذا كان بعِلمٍ', '', '', 'c', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'ما مَعنَى "شَعَائِر":', '', 'أُصُولُ الدِّينِ وأَركانُه', '', 'ما جعَله اللهُ تعالى عَلَمًا لطاعتِه', '', 'جمْعُ شَعيرَةٍ؛ والمرادُ بها الصَّلواتُ الخَمسِ', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'المُرادُ من قَولِه تَعالَى: {وَجِلَتْ قُلُوبُهُمْ}:', '', 'خَافَتْ', '', 'صَقَلَتْ', '', 'فَرِحَت', '', '', 'a', 'easy');
