(@tafseer_category_id, '', 'مَعنَى أرذلِ العُمُر:', '', 'أَرْدَأُ العُمُر', '', 'آخِرُ العُمُر', '', 'أَنفَعُ العُمُر', '', '', 'a', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'إذا غَرَبتِ الشَّمسُ فإنها:', '', 'تَشتَكِي إلى رَبِّها ممَّن سَجدُوا لها مِن دُونِ الله', '', 'تذهَبُ حتى تسجُدَ تحتَ العَرشِ', '', 'تَذهَبُ للبَيتِ المَعمُورِ فتَطوفُ بِه', '', '', 'b', 'easy');
INSERT INTO questions (category_id, question_text, question_text_ar, option_a, option_a_ar, option_b, option_b_ar, option_c, option_c_ar, option_d, option_d_ar, correct_answer, difficulty) VALUES
(@tafseer_category_id, '', 'في قَولِه تَعالَى: {وَعَلَى كُلِّ ضَامِرٍ} الضَّامِرُ هُوَ:', '', 'السَّحابُ الذي يَحمِلُ المَطَرَ', '', 'كُلُّ ما أُضْمِرَ في النَّفْسِ', '', 'البَعيرُ المهزولُ الَّذي أتْعَبه السَّفرُ', '', '', 'c', 'easy');
