(@tafseer_category_id, '', 'المقصود من قوله تعالى: {ثَانِيَ عِطْفِهِ}:', '', 'الفَخرُ', '', 'الرِّياءُ', '', 'التَّكَبُّر', '', '', 'c', 'easy');
